import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPNode:

        def __init__(self, value, count=1, parent=None, children=None):
            self.value = value
            self.count = count
            self.parent = parent
            self.children = defaultdict(FPNode) if children is None else children

        def increment(self):
            self.count += 1

    def build_fp_tree(transactions, min_sup):
        item_counts = Counter((item for transaction in transactions for item in transaction))
        filtered_transactions = [[item for item in transaction if item_counts[item] >= min_sup] for transaction in transactions]
        sorted_items = sorted(filtered_transactions[0], key=lambda x: (-item_counts[x], x))
        root = FPNode(None)
        for transaction in filtered_transactions:
            current_node = root
            for item in sorted_items:
                if item in transaction:
                    if item not in current_node.children:
                        current_node.children[item] = FPNode(item, parent=current_node)
                    current_node = current_node.children[item]
                    current_node.increment()
        return (root, item_counts)

    def generate_conditional_pattern_bases(node, prefix, conditional_patterns):
        if node.parent is not None:
            conditional_patterns.append((prefix + (node.value,), node.count))
            generate_conditional_pattern_bases(node.parent, prefix, conditional_patterns)

    def build_conditional_fp_tree(conditional_patterns, min_sup):
        root = FPNode(None)
        for (pattern, count) in conditional_patterns:
            current_node = root
            for item in pattern:
                if item not in current_node.children:
                    current_node.children[item] = FPNode(item, parent=current_node)
                current_node = current_node.children[item]
                current_node.increment()
        return root

    def mine_fp_tree(root, prefix, frequent_itemsets, min_sup):
        nodes = []
        for node in root.children.values():
            nodes.extend(node.children.values())
        nodes.sort(key=lambda x: (-x.count, x.value))
        if len(nodes) == 1 and nodes[0].value is not None:
            path = [(nodes[0].value,)]
            while nodes[0].parent is not None:
                path.append((nodes[0].parent.value,))
                nodes[0] = nodes[0].parent
            path.reverse()
            for r in range(1, len(path) + 1):
                for combination in itertools.combinations(path, r):
                    frequent_itemsets.add(tuple(prefix) + tuple(combination))
        for node in nodes:
            if node.value is not None:
                conditional_patterns = []
                generate_conditional_pattern_bases(node, (), conditional_patterns)
                if conditional_patterns:
                    conditional_root = build_conditional_fp_tree(conditional_patterns, min_sup)
                    mine_fp_tree(conditional_root, prefix + (node.value,), frequent_itemsets, min_sup)
    if not transactions or min_sup <= 0:
        return []
    (fp_tree, item_counts) = build_fp_tree(transactions, min_sup)
    frequent_itemsets = set()
    mine_fp_tree(fp_tree, (), frequent_itemsets, min_sup)
    result = sorted(frequent_itemsets, key=lambda x: (len(x), x))
    return result
