import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)

    def c(n):
        if n == 1:
            return 0
        else:
            return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def path_length(x, node):
        if node is None:
            return 0
        if len(node['data']) == 1:
            return c(len(node['data']))
        feature = node['feature']
        threshold = node['threshold']
        if x[feature] < threshold:
            return 1 + path_length(x, node['left'])
        else:
            return 1 + path_length(x, node['right'])

    def build_tree(data, depth=0, height_limit=None):
        if height_limit is None:
            height_limit = math.ceil(math.log2(len(data)))
        if depth >= height_limit or len(data) <= 1:
            return {'data': data}
        feature = np.random.randint(data.shape[1])
        min_val = np.min(data[:, feature])
        max_val = np.max(data[:, feature])
        threshold = np.random.uniform(min_val, max_val)
        left_data = data[data[:, feature] < threshold]
        right_data = data[data[:, feature] >= threshold]
        node = {'feature': feature, 'threshold': threshold, 'left': build_tree(left_data, depth + 1, height_limit), 'right': build_tree(right_data, depth + 1, height_limit)}
        return node
    n_samples = data.shape[0]
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    scores = np.zeros(n_samples)
    for _ in range(n_trees):
        if sample_size >= n_samples:
            subsample = data
        else:
            subsample_indices = np.random.choice(n_samples, sample_size, replace=False)
            subsample = data[subsample_indices]
        tree = build_tree(subsample)
        for i in range(n_samples):
            scores[i] += path_length(data[i], tree)
    scores /= n_trees
    scores = 2 ** (-scores / phi)
    threshold = np.percentile(scores, (1 - epsilon) * 100)
    outliers = np.where(scores >= threshold)[0].tolist()
    outliers.sort()
    return outliers

