from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    feature_value_label_pairs = set()
    for (x, y) in zip(X_train, y_train):
        for (i, v) in enumerate(x):
            if v != 0:
                feature_value_label_pairs.add((i, v, y))
    empirical_counts = defaultdict(float)
    total_counts = defaultdict(float)
    for (x, y) in zip(X_train, y_train):
        for (i, v) in enumerate(x):
            if v != 0:
                empirical_counts[i, v, y] += 1
                total_counts[y] += 1
    empirical_dists = {pair: count / total_counts[y] for (pair, count) in empirical_counts.items()}
    weights = {pair: 0.0 for pair in feature_value_label_pairs}
    for _ in range(n_iter):
        conditional_probs = defaultdict(lambda : defaultdict(float))
        for (x, y) in zip(X_train, y_train):
            z_x = 0.0
            for (i, v) in enumerate(x):
                if v != 0:
                    z_x += sum((math.exp(weights[i, v, y]) for y in set(y_train)))
            for (i, v) in enumerate(x):
                if v != 0:
                    for y in set(y_train):
                        conditional_probs[x, y][i, v, y] = math.exp(weights[i, v, y]) / z_x
        max_update = 0.0
        for pair in feature_value_label_pairs:
            (i, v, y) = pair
            update = empirical_dists[pair] - sum((conditional_probs[x][pair] for x in X_train if x[i] == v))
            weights[pair] += update
            max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        scores = defaultdict(float)
        for (i, v) in enumerate(x):
            if v != 0:
                for y in set(y_train):
                    scores[y] += weights[i, v, y]
        predicted_label = max(scores, key=scores.get)
        predictions.append(predicted_label)
    return predictions
