import numpy as np
from typing import Union
def tanh(z: Union[int, float, np.ndarray, list]) -> Union[float, list]:
    """Compute the hyperbolic tangent of *z* without using ``numpy.tanh``.

    The function must accept:
        * A scalar ``int`` or ``float``
        * A Python list (including nested lists)
        * A ``numpy.ndarray`` of any shape

    Every output value has to be rounded to 4 decimal places.

    If *z* is a scalar return a single ``float``; otherwise return a Python
    ``list`` obtained via ``numpy.ndarray.tolist``.

    Args:
        z: Numerical input (scalar, list, or np.ndarray).

    Returns:
        Numerical output with the same structure as the input, but rounded
        to four decimals and converted to ``list`` if the input was not a
        scalar.
    """

    def exp(x):
        return np.exp(x)

    def power_of_two(n):
        return exp(np.log(2) * n)

    def pow(x, y):
        return exp(y * np.log(x))

    def log(x):
        return np.log(x)

    def sign(x):
        return 1 if x >= 0 else -1

    def sinh(x):
        return (exp(x) - exp(-x)) / 2

    def cosh(x):
        return (exp(x) + exp(-x)) / 2

    def tanh_recursive(x):
        if abs(x) < 1e-06:
            return 0
        elif x > 0:
            return 1 - 2 * tanh_recursive((x - 1) / 2)
        else:
            return 2 * tanh_recursive((-x - 1) / 2) - 1

    def tanh_nonrecursive(x):
        if abs(x) < 1e-06:
            return 0
        elif x > 0:
            return (exp(2 * x) - 1) / (exp(2 * x) + 1)
        else:
            return (1 - exp(2 * x)) / (exp(2 * x) + 1)

    def tanh_numpy(z):
        return np.tanh(z)

    def round_to_four_decimal_places(value):
        return round(value, 4)
    if isinstance(z, int) or isinstance(z, float):
        result = tanh_nonrecursive(float(z))
    elif isinstance(z, list):
        result = [round_to_four_decimal_places(tanh_nonrecursive(float(item))) for item in z]
    elif isinstance(z, np.ndarray):
        result = tanh_numpy(z).astype(float)
    else:
        raise ValueError('Unsupported input type')
    if isinstance(result, np.ndarray):
        result = result.tolist()
    return result
