class Node:

    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None

    def __lt__(self, other):
        return self.val < other.val

    def __le__(self, other):
        return self.val <= other.val

    def __gt__(self, other):
        return self.val > other.val

    def __ge__(self, other):
        return self.val >= other.val
def inorder_keys(pairs):
    """Build a BST from (key, value) *pairs* and return keys from an in-order traversal.

    Args:
        pairs (list[tuple[Any, int | float]]): List of (key, numeric value) pairs.

    Returns:
        list: Keys in in-order (ascending by numeric value). Return an empty
        list if *pairs* is empty.
    """
    if not pairs:
        return []
    root = Node(pairs[0][1])
    key_to_node = {pairs[0][0]: root}
    for (key, value) in pairs[1:]:
        node = Node(value)
        key_to_node[key] = node
        current = root
        while True:
            if node < current:
                if current.left is None:
                    current.left = node
                    break
                else:
                    current = current.left
            elif current.right is None:
                current.right = node
                break
            else:
                current = current.right

    def in_order_traversal(node):
        if node is not None:
            in_order_traversal(node.left)
            result.append(key_to_node[node.val].key)
            in_order_traversal(node.right)
    result = []
    in_order_traversal(root)
    return result
