import re
def apply_activation(x, param=None):
    """Apply a chosen activation to the input vector.

    Args:
        x (list[float] | np.ndarray): Input data. Anything that can be
            converted to a NumPy array of floats is accepted.
        param (str | None, optional): Description of the activation. See the
            task specification for the accepted values. If *None* the
            identity function is applied.

    Returns:
        list[float]: Result of applying the activation element-wise, rounded
            to six decimals and converted to a standard Python *list*.
    """

    def identity(x):
        return x

    def relu(x):
        return np.maximum(0, x)

    def tanh(x):
        return np.tanh(x)

    def logistic_sigmoid(x):
        return 1 / (1 + np.exp(-x))

    def leaky_relu(x, alpha=0.01):
        return np.where(x > 0, x, alpha * x)

    def affine(x, slope=1.0, intercept=0.0):
        return slope * x + intercept
    x_array = np.array(x).astype(float)
    if param is None or param.lower() == 'identity':
        return [round(i, 6) for i in identity(x_array)]
    if isinstance(param, str):
        match = re.match('leaky relu\\(alpha=(\\d+\\.\\d+)\\)', param.lower())
        if match:
            alpha = float(match.group(1))
            return [round(i, 6) for i in leaky_relu(x_array, alpha)]
        match = re.match('affine\\(slope=(\\d+\\.\\d+), intercept=(\\d+\\.\\d+)\\)', param.lower())
        if match:
            slope = float(match.group(1))
            intercept = float(match.group(2))
            return [round(i, 6) for i in affine(x_array, slope, intercept)]
    if param.lower() == 'relu':
        return [round(i, 6) for i in relu(x_array)]
    elif param.lower() == 'tanh':
        return [round(i, 6) for i in tanh(x_array)]
    elif param.lower() == 'sigmoid':
        return [round(i, 6) for i in logistic_sigmoid(x_array)]
    else:
        raise ValueError(f'Unknown activation: {param}')
