import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """

    def validate_inputs(X, Y, sigma):
        if Y is not None and len(X[0]) != len(Y[0]):
            return False
        if sigma is None:
            sigma = np.sqrt(len(X[0]) / 2)
        elif isinstance(sigma, (int, float)):
            if sigma <= 0:
                return False
        elif isinstance(sigma, list):
            if len(sigma) != len(X[0]):
                return False
            if any((s <= 0 for s in sigma)):
                return False
        return True
    if not validate_inputs(X, Y, sigma):
        return -1
    if Y is None:
        Y = X
    (N, C) = (len(X), len(X[0]))
    (M, _) = (len(Y), len(Y[0]))
    K = np.zeros((N, M))
    for i in range(N):
        for j in range(M):
            dist_squared = sum(((xi - yi) ** 2 for (xi, yi) in zip(X[i], Y[j])))
            K[i, j] = np.exp(-0.5 * dist_squared / sigma)
    return round_to_four_decimal_places(K)
def round_to_four_decimal_places(matrix):
    """Round each element of the matrix to four decimal places."""
    return [[round(element, 4) for element in row] for row in matrix]
