import numpy as np
def is_stochastic(X: list[list[float]] | 'np.ndarray') -> bool:
    """Return True if *X* is a row-stochastic matrix, otherwise False.

    A matrix is row-stochastic when every element is a probability (0 ≤ p ≤ 1)
    and each row sums to 1 (within a small numerical tolerance).

    Args:
        X: Matrix given as a list of lists or a NumPy array.

    Returns:
        bool: True if *X* is row-stochastic, False otherwise.
    """
    if isinstance(X, np.ndarray):
        if not np.all((X >= 0) & (X <= 1)):
            return False
        if not np.allclose(np.sum(X, axis=1), 1, atol=1e-08):
            return False
    else:
        try:
            X = np.array(X)
        except ValueError:
            raise ValueError('Input must be a list of lists or a NumPy array.')
        if not np.all((X >= 0) & (X <= 1)):
            return False
        if not np.allclose(np.sum(X, axis=1), 1, atol=1e-08):
            return False
    return True

import numpy as np
def softplus(x, deriv: bool=False):
    """
    Compute the SoftPlus activation function or its gradient.

    Parameters:
    x (scalar or list/array): Input data for which to compute the SoftPlus.
    deriv (bool): Flag indicating whether to compute the gradient (True) or the SoftPlus itself (False).

    Returns:
    scalar/list/array: The SoftPlus output or gradient, rounded to 4 decimal places.
    """
    if isinstance(x, (int, float)):
        x = np.array([x])
    if deriv:
        exp_x = np.exp(x)
        sigm = 1 / (1 + exp_x)
        return round(sigm, 4)
    else:
        log_sum_exp = np.log1p(exp_x)
        return round(log_sum_exp, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    frequent_items_sorted = sorted(frequent_items.keys())
    root = defaultdict(int)
    for transaction in transactions:
        path = root
        for item in frequent_items_sorted:
            if item in transaction:
                path[item] += 1
                path = path[item]

    def build_conditional_tree(header_table, path):
        conditional_tree = defaultdict(int)
        for item in header_table:
            if path[item]:
                conditional_tree[item] = path[item]
                conditional_tree[item].update(path[item])
                del path[item]
        return conditional_tree

    def find_frequent_item_sets(tree, header_table, path, prefix, min_support):
        frequent_item_sets = []
        for item in header_table:
            if path[item]:
                new_path = path.copy()
                new_path[item] -= 1
                new_prefix = prefix + [item]
                if len(new_prefix) > 1:
                    frequent_item_sets.append(sorted(new_prefix))
                else:
                    frequent_item_sets.extend(find_frequent_item_sets(conditional_tree[item], header_table[item], new_path, new_prefix, min_support))
        return frequent_item_sets
    frequent_item_sets = []
    for i in range(len(frequent_items_sorted)):
        (head, tail) = (frequent_items_sorted[:i + 1], frequent_items_sorted[i + 1:])
        header_table = defaultdict(list)
        for item in head:
            header_table[tree[item]].append(item)
        for item in header_table:
            path = tree[item]
            conditional_tree = build_conditional_tree(header_table, path)
            frequent_item_sets.extend(find_frequent_item_sets(conditional_tree, header_table[item], path, [], min_support))
    frequent_item_sets.sort(key=lambda x: (len(x), x))
    return frequent_item_sets

import numpy as np
def knn_predict(X: np.ndarray, y: np.ndarray, X_test: np.ndarray, k: int=3, metric: str='euclidean') -> list:
    """Predict labels for *X_test* using the k-Nearest Neighbours algorithm.

    Args:
        X: 2-D NumPy array of shape (n_samples, n_features) containing the
           training features.
        y: 1-D NumPy array of length *n_samples* containing the training labels.
        X_test: 2-D NumPy array of shape (m_samples, n_features) with the test
                 samples whose labels are to be predicted.
        k: Number of neighbours to consider (default: 3).  If *k* exceeds the
           number of training samples, use all samples instead.
        metric: Distance metric to use – 'euclidean', 'manhattan', or 'cosine'.

    Returns:
        A Python list containing the predicted label for each test sample, in
        the same order as *X_test*.
    """

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2))

    def manhattan_distance(x1, x2):
        return np.sum(np.abs(x1 - x2))

    def cosine_distance(x1, x2, epsilon=1e-12):
        dot_product = np.dot(x1, x2)
        norm_x = np.linalg.norm(x1, ord=2)
        norm_y = np.linalg.norm(x2, ord=2)
        if norm_x == 0 or norm_y == 0:
            return 1
        return 1 - dot_product / (norm_x * norm_y + epsilon)
    num_train_samples = len(y)
    predictions = []
    for x_test in X_test:
        if k > num_train_samples:
            distances = [metric(x_test, train_sample) for train_sample in X]
        else:
            distances = sorted([(metric(x_test, train_sample), idx) for (idx, train_sample) in enumerate(X)], key=lambda item: item[0])
        nearest_indices = [distances[i][1] for i in range(k)]
        neighbor_labels = [y[idx] for idx in nearest_indices]
        label_counts = {}
        for label in neighbor_labels:
            if label in label_counts:
                label_counts[label] += 1
            else:
                label_counts[label] = 1
        predicted_label = min(label_counts, key=label_counts.get)
        predictions.append(predicted_label)
    return predictions.tolist()

import numpy as np
class DecisionStump:

    def __init__(self):
        self.feature_index = None
        self.threshold = None
        self.polarity = None

    def fit(self, X, y):
        (n_samples, n_features) = X.shape
        min_error = float('inf')
        for feature_idx in range(n_features):
            thresholds = np.unique(X[:, feature_idx])
            for threshold in thresholds:
                left_mask = X[:, feature_idx] <= threshold
                right_mask = X[:, feature_idx] > threshold
                left_predictions = 1 if np.sum(y[left_mask]) >= np.sum(y[~left_mask]) else -1
                right_predictions = -1 if np.sum(y[left_mask]) >= np.sum(y[~left_mask]) else 1
                errors = np.where(y != left_predictions, 1, 0)
                error = np.sum(errors * left_mask) + np.sum(errors * ~left_mask)
                if error < min_error:
                    min_error = error
                    self.feature_index = feature_idx
                    self.threshold = threshold
                    self.polarity = left_predictions
        if min_error == float('inf'):
            self.polarity = None

    def predict(self, X):
        if self.polarity is None:
            raise ValueError('Decision stump has not been trained.')
        predictions = []
        for x in X:
            if x[self.feature_index] <= self.threshold:
                prediction = self.polarity
            else:
                prediction = -self.polarity
            predictions.append(prediction)
        return np.array(predictions)
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Fill in here. The final implementation must follow the specification given in the task
    description and return a list with the predicted class labels for *X_test*."""
    (n_samples, n_features) = X_train.shape
    weights = np.full(n_samples, 1 / n_samples)
    predictions = np.zeros(len(X_test))
    for t in range(n_estimators):
        stump = DecisionStump()
        stump.fit(X_train, y_train)
        predictions_stump = stump.predict(X_train)
        errors = np.where(y_train != predictions_stump, 1, 0)
        error_t = np.dot(weights, errors)
        if error_t == 0:
            break
        alpha_t = 0.5 * np.log((1 - error_t) / (error_t + 1e-10))
        weights *= np.exp(-alpha_t * y_train * predictions_stump)
        weights /= np.sum(weights)
        predictions += alpha_t * stump.predict(X_test)
    predictions = (predictions > 0).astype(int)
    return predictions.tolist()

import numpy as np
def gini_impurity(y):
    """Calculate Gini impurity for a given array of class labels."""
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def find_best_split(X, y, feature_index):
    """Find the best split point for a given feature."""
    best_gain = 0
    best_feature_value = None
    (best_left_y, best_right_y) = ([], [])
    sorted_indices = np.argsort(X[:, feature_index])
    sorted_X = X[sorted_indices]
    sorted_y = y[sorted_indices]
    cumulative_gini = np.cumsum(gini_impurity(sorted_y))
    for i in range(len(sorted_X) - 1):
        left_y = sorted_y[:i + 1]
        right_y = sorted_y[i + 1:]
        weight_left = len(left_y) / len(sorted_y)
        weight_right = len(right_y) / len(sorted_y)
        current_gain = cumulative_gini[-1] - (weight_left * cumulative_gini[i + 1] + weight_right * cumulative_gini[i])
        if current_gain > best_gain:
            best_gain = current_gain
            best_feature_value = sorted_X[i, feature_index]
            best_left_y = left_y
            best_right_y = right_y
    return (best_feature_value, best_left_y, best_right_y)
