def build_decision_tree(X, y, max_depth=None, current_depth=0):
    """Recursively build the decision tree."""
    unique_labels = np.unique(y)
    if len(unique_labels) == 1 or (max_depth is not None and current_depth >= max_depth):
        return TreeNode(value=unique_labels[0])
    (best_feature_index, best_left_y, best_right_y) = find_best_split(X, y, np.random.choice(X.shape[1]))
    node = TreeNode(best_feature_index)
    node.left = build_decision_tree(X[y == best_feature_index], y[y == best_feature_index], max_depth=max_depth, current_depth=current_depth + 1)
    node.right = build_decision_tree(X[y != best_feature_index], y[y != best_feature_index], max_depth=max_depth, current_depth=current_depth + 1)
    return node
def decision_tree_predict(X_train, y_train, X_test, max_depth=None):
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    root = build_decision_tree(X_train, y_train, max_depth=max_depth)

    def traverse(node, x):
        """Traverse the tree to predict the class label for a given input."""
        if node.value is not None:
            return node.value
        feature_index = node.value
        if x[feature_index] < node.left.value:
            return traverse(node.left, x)
        else:
            return traverse(node.right, x)
    predictions = [traverse(root, x) for x in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        if not isinstance(x, (list, tuple, np.ndarray)):
            return False
        if not isinstance(y, (list, tuple, np.ndarray)):
            return False
        x = np.array(x)
        y = np.array(y)
        if len(x) != len(y):
            return False
        if np.any(np.isnan(x)) or np.any(np.isnan(y)):
            return False
        if name == 'cosine' and (np.linalg.norm(x) == 0 or np.linalg.norm(y) == 0):
            return False
        return True

    def euclidean(x, y):
        if not validate_vectors(x, y):
            return -1
        return round(np.linalg.norm(x - y), 4)

    def manhattan(x, y):
        if not validate_vectors(x, y):
            return -1
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        if not validate_vectors(x, y):
            return -1
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        if not validate_vectors(x, y):
            return -1
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        dot_product = np.dot(x, y)
        return round(1 - dot_product / (norm_x * norm_y), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    return metrics.get(name, lambda _: -1)

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def apply_leaky_relu(x):
        if isinstance(x, (list, tuple)):
            return [apply_leaky_relu(sub_x) for sub_x in x]
        elif isinstance(x, np.ndarray):
            return x.clip(0, None).tolist() + a * x.clip(None, 0).tolist()
        else:
            return max(x, a * x)
    return apply_leaky_relu(z)

from collections import defaultdict
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    sorted_frequent_items = sorted(frequent_items.items(), key=lambda x: (-x[1], x[0]))

    def create_fp_tree(data):
        root = {'support': 0, 'children': {}}
        for transaction in data:
            current_node = root
            for item in sorted_frequent_items:
                while item[0] in current_node['children']:
                    current_node = current_node['children'][item[0]]
                if item[0] not in current_node['children']:
                    new_node = {'support': 0, 'children': {}}
                    current_node['children'][item[0]] = new_node
                current_node = current_node['children'][item[0]]
                current_node['support'] += 1
        return root
    return create_fp_tree(transactions)
