import numpy as np
def conv2D(X: 'np.ndarray', W: 'np.ndarray', stride: int, pad, dilation: int=0):
    """Performs a 2-D convolution (cross-correlation).

    Args:
        X: NumPy array of shape ``(n_ex, in_rows, in_cols, in_ch)`` representing the input batch.
        W: NumPy array of shape ``(kernel_rows, kernel_cols, in_ch, out_ch)`` containing the kernels.
        stride: Stride of the convolution.
        pad: Padding specification – integer, tuple or the string ``'same'``.
        dilation: Optional dilation factor. ``0`` corresponds to a normal convolution.

    Returns:
        The convolution result **as a Python list** obtained through ``numpy.ndarray.tolist()``.
    """

    def calculate_padding(pad_spec, in_rows, in_cols):
        if isinstance(pad_spec, int):
            return (pad_spec, pad_spec)
        elif isinstance(pad_spec, tuple):
            if len(pad_spec) == 2:
                return (pad_spec[0], pad_spec[1])
            elif len(pad_spec) == 4:
                return (pad_spec[0], pad_spec[1], pad_spec[2], pad_spec[3])
        elif pad_spec == 'same':
            padding_rows = max(0, (in_rows - 1) * stride + dilation * (W.shape[0] - 1) + 1 - in_rows)
            padding_cols = max(0, (in_cols - 1) * stride + dilation * (W.shape[1] - 1) + 1 - in_cols)
            return (padding_rows // 2, padding_rows - padding_rows // 2, padding_cols // 2, padding_cols - padding_cols // 2)
        else:
            raise ValueError('Invalid padding specification')
    if isinstance(pad, str):
        padding = calculate_padding(pad, X.shape[1], X.shape[2])
    else:
        padding = calculate_padding(pad, X.shape[1], X.shape[2])
    X_padded = np.pad(X, ((0, 0), (padding[0], padding[1]), (padding[2], padding[3]), (0, 0)), mode='constant')
    (n_ex, in_rows, in_cols, in_ch) = X_padded.shape
    (kernel_rows, kernel_cols, _, out_ch) = W.shape
    out_rows = (in_rows - kernel_rows + 2 * padding[0]) // stride + 1
    out_cols = (in_cols - kernel_cols + 2 * padding[2]) // stride + 1
    Y = np.zeros((n_ex, out_rows, out_cols, out_ch))
    for i in range(n_ex):
        for c_out in range(out_ch):
            for r_out in range(out_rows):
                for c_out in range(out_cols):
                    submatrix = X_padded[i, r_out * stride:r_out * stride + kernel_rows, c_out * stride:c_out * stride + kernel_cols]
                    Y[i, r_out, c_out, c_out] = np.sum(submatrix * W[:, :, :, c_out])
    return Y.tolist()
