def determinant_4x4(matrix: list[list[int | float]]) -> float:

    def determinant_3x3(submatrix: list[list[int | float]]) -> float:
        return submatrix[0][0] * (submatrix[1][1] * submatrix[2][2] - submatrix[1][2] * submatrix[2][1]) - submatrix[0][1] * (submatrix[1][0] * submatrix[2][2] - submatrix[1][2] * submatrix[2][0]) + submatrix[0][2] * (submatrix[1][0] * submatrix[2][1] - submatrix[1][1] * submatrix[2][0])
    if len(matrix) == 4 and len(matrix[0]) == 4:
        det = 0
        for col in range(4):
            submatrix = [row[1:] for row in matrix[1:]]
            det += (-1) ** col * matrix[0][col] * determinant_3x3(submatrix)
        return det
    else:
        raise ValueError('Input matrix must be a 4x4 matrix')
