def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:

    def entropy(probs):
        return -sum((p * math.log2(p) for p in probs if p > 0))

    def information_gain(examples, attr, target_attr):
        total_entropy = entropy([examples.count(e) / len(examples) for e in set([e[target_attr] for e in examples])])
        values = set([e[attr] for e in examples])
        new_entropy = 0
        for value in values:
            subset = [e for e in examples if e[attr] == value]
            prob = len(subset) / len(examples)
            new_entropy += prob * entropy([subset.count(e) / len(subset) for e in set([e[target_attr] for e in subset])])
        return total_entropy - new_entropy
    if len(set([e[target_attr] for e in examples])) == 1:
        return examples[0][target_attr]
    if not attributes:
        return Counter([e[target_attr] for e in examples]).most_common(1)[0][0]
    best_attr = max(attributes, key=lambda attr: information_gain(examples, attr, target_attr))
    tree = {best_attr: {}}
    values = set([e[best_attr] for e in examples])
    for value in values:
        subset = [e for e in examples if e[best_attr] == value]
        remaining_attributes = [attr for attr in attributes if attr != best_attr]
        tree[best_attr][value] = learn_decision_tree(subset, remaining_attributes, target_attr)
    return tree
