def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    df = {}
    for doc in corpus:
        for term in set(doc):
            if term not in df:
                df[term] = 0
            df[term] += 1
    num_docs = len(corpus)
    tf_idf_matrix = []
    for doc in corpus:
        doc_tf_idf = []
        for term in query:
            if term in doc:
                tf = doc.count(term) / len(doc)
                idf = np.log((num_docs + 1) / (df.get(term, 0) + 1)) + 1
                tf_idf = tf * idf
            else:
                tf_idf = 0
            doc_tf_idf.append(round(tf_idf, 4))
        tf_idf_matrix.append(doc_tf_idf)
    return tf_idf_matrix
