def calculate_brightness(img):
    if not img or not all((isinstance(row, list) for row in img)):
        return -1
    total_pixels = 0
    total_brightness = 0
    for row in img:
        if len(row) != len(img[0]):
            return -1
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
            total_pixels += 1
            total_brightness += pixel
    if total_pixels == 0:
        return -1
    average_brightness = total_brightness / total_pixels
    return round(average_brightness, 2)
