def create_row_hv(row, dim, random_seeds):

    def create_hv(feature, value, dim, seed):
        np.random.seed(seed)
        hv = np.random.randint(2, size=dim)
        return hv

    def bind_hv(hv1, hv2):
        return np.bitwise_xor(hv1, hv2)
    composite_hv = np.zeros(dim, dtype=int)
    for (feature, value) in row.items():
        feature_hv = create_hv(feature, value, dim, random_seeds[feature])
        composite_hv = bind_hv(composite_hv, feature_hv)
    return composite_hv.tolist()
