def gen_text(prompt: str, n_tokens_to_generate: int=40):
    np.random.seed(42)
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    token_ids = encoder.encode(prompt)
    token_ids = token_ids + [0] * (hparams['n_ctx'] - len(token_ids))
    generated_text = prompt
    for _ in range(n_tokens_to_generate):
        token_embeddings = np.dot(params['wte'], np.eye(len(token_ids))[token_ids])
        pos_embeddings = params['wpe'][:len(token_ids)]
        embeddings = token_embeddings + pos_embeddings
        attention_output = np.random.rand(len(token_ids), len(token_ids))
        ff_output = np.dot(params['wte'], attention_output)
        ln_output = (ff_output - np.mean(ff_output, axis=1, keepdims=True)) / np.std(ff_output, axis=1, keepdims=True)
        next_token_id = np.argmax(ln_output[-1])
        next_token = encoder.decode([next_token_id])
        generated_text += ' ' + next_token
        token_ids = token_ids[1:] + [next_token_id]
    return generated_text
