def pattern_weaver(n, crystal_values, dimension):

    def softmax(values):
        e_values = np.exp(values - np.max(values))
        return e_values / e_values.sum()
    attention_scores = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            if i != j:
                dot_product = np.dot(crystal_values[i], crystal_values[j])
                attention_scores[i, j] = softmax(dot_product)[0]
    weighted_patterns = np.zeros(n)
    for i in range(n):
        weighted_patterns[i] = np.sum(attention_scores[i] * crystal_values[i])
    return [round(pattern, 4) for pattern in weighted_patterns]
