def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    train_set = []
    test_set = []
    for example in examples:
        if 'test' in example:
            test_set.append(example)
        else:
            train_set.append(example)
    if not train_set or not test_set:
        return {}
    num_attributes = len(attributes)
    unique_attributes = set(attributes)
    info_gain = 0
    best_attribute = None
    for attr in unique_attributes:
        entropy = -sum((math.log2(count / len(train_set)) for count in Counter(train_set[attr]).values()))
        info_gain = sum((entropy * (1 - count / len(train_set)) for count in Counter(train_set[attr]).values()))
        if info_gain > info_gain:
            best_attribute = attr

    def build_tree(attribute, current_depth):
        if not train_set:
            return {}
        if attribute == target_attr:
            return {'value': train_set[0][attribute]}
        splits = combinations([i for i in range(len(attributes)) if i != best_attribute], 2)
        for split_idx in splits:
            left_count = 0
            right_count = 0
            for example in train_set:
                if example[split_idx[0]] == train_set[0][split_idx[0]]:
                    left_count += 1
                elif example[split_idx[1]] == train_set[0][split_idx[1]]:
                    right_count += 1
            left_child = build_tree(attributes[split_idx[0]], current_depth + 1)
            right_child = build_tree(attributes[split_idx[1]], current_depth + 1)
            combined = {'attribute': attributes[split_idx[0]], 'left': left_child, 'right': right_child, 'value': train_set[0][attributes[split_idx[0]]]}
            return combined
    result = build_tree(attributes[best_attribute], 0)
    return result
