def rref(matrix):
    matrix = np.array(matrix)
    matrix = np.tolist(matrix)
    (n_rows, n_cols) = matrix.shape
    for i in range(n_rows):
        pivot_col = i
        if not np.any(matrix[i]):
            break
        pivot_row = np.where(matrix[i] != 0)[0][0]
        matrix[[i], pivot_col] = matrix[pivot_row, [i], pivot_col]
        for j in range(i + 1, n_rows):
            if matrix[j, pivot_col] != 0:
                matrix[j, pivot_col] = 0
    return matrix.tolist()
