import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def log_link(mu):
        return np.log(mu)

    def logit_link(mu):
        return np.log(mu / (1 - mu))

    def identity_inv_link(eta):
        return eta

    def log_inv_link(eta):
        return np.exp(eta)

    def logit_inv_link(eta):
        return 1 / (1 + np.exp(-eta))

    def identity_deriv_link(mu):
        return np.ones_like(mu)

    def log_deriv_link(mu):
        return mu / (mu * (1 - mu))

    def logit_deriv_link(mu):
        return (1 - mu) / (mu * (1 - mu))
    return {'identity': {'link': identity_link, 'inv_link': identity_inv_link, 'deriv_link': identity_deriv_link}, 'log': {'link': log_link, 'inv_link': log_inv_link, 'deriv_link': log_deriv_link}, 'logit': {'link': logit_link, 'inv_link': logit_inv_link, 'deriv_link': logit_deriv_link}}

import math
from collections import Counter
from itertools import combinations
from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """
    Return all frequent itemsets in the given transactions based on the minimum support threshold.

    Args:
        transactions: List of lists representing transactions.
        minsup: Minimum support as a fraction between 0 and 1.

    Returns:
        A dictionary mapping frequent itemsets (as tuples) to their support counts.
    """
    transaction_sets = [frozenset(transaction) for transaction in transactions]
    num_transactions = len(transaction_sets)
    min_support = math.ceil(minsup * num_transactions)
    item_support = Counter()
    for transaction in transaction_sets:
        for item in transaction:
            item_support[item] += 1
    frequent_items = {}
    for (item, count) in item_support.items():
        if count >= min_support:
            frequent_items[item,] = count
    frequent_pairs = {}
    for (item1, item2) in combinations(item_support.keys(), 2):
        pair = tuple(sorted((item1, item2)))
        common_transactions = 0
        for transaction in transaction_sets:
            if item1 in transaction and item2 in transaction:
                common_transactions += 1
        if common_transactions >= min_support:
            frequent_pairs[pair] = common_transactions
    result = frequent_items.copy()
    result.update(frequent_pairs)
    return result

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings.  Any string
    should be converted with `float()`; existing numeric values stay as they
    are.  The pair `(a, b)` must then be returned.
    """
    if isinstance(a, str) and a.replace('.', '', 1).isdigit():
        a = float(a)
    elif isinstance(a, str):
        a = float(a)
    if isinstance(b, str) and b.replace('.', '', 1).isdigit():
        b = float(b)
    elif isinstance(b, str):
        b = float(b)
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    return a + b

import numpy as np
def is_stochastic(X: list[list[float]] | 'np.ndarray') -> bool:
    """Return True if *X* is a row-stochastic matrix, otherwise False.

    A matrix is row-stochastic when every element is a probability (0 ≤ p ≤ 1)
    and each row sums to 1 (within a small numerical tolerance).

    Args:
        X: Matrix given as a list of lists or a NumPy array.

    Returns:
        bool: True if *X* is row-stochastic, False otherwise.
    """
    if not isinstance(X, (list, np.ndarray)):
        return False
    if not isinstance(X, np.ndarray) and (not isinstance(X, list)):
        return False
    if isinstance(X, np.ndarray):
        X = X.astype(float)
    if not isinstance(X, np.ndarray) and (not isinstance(X, list)):
        return False
    if isinstance(X, np.ndarray):
        if X.ndim != 2:
            return False
    if not np.all(np.logical_and(X >= 0, X <= 1)):
        return False
    if not np.allclose(X.sum(axis=1), np.ones(X.shape[1]), atol=1e-08):
        return False
    return True

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> List[Number]:
    """Return the k smallest numbers contained in *nums*.

    The function should scan the input list once, maintaining a custom
    max-heap (size at most k) implemented with a Python list.  At the end,
    return the k smallest elements in ascending order.  If *k* is invalid
    (≤0 or larger than len(nums)) return -1.

    The use of the built-in ``heapq`` module is **not** allowed.
    """
    if k <= 0 or k > len(nums):
        return -1
    max_heap = []

    def _move_up(i: int) -> None:
        while i > 0:
            parent = (i - 1) // 2
            if nums[max_heap[parent]] <= nums[max_heap[i]]:
                break
            (max_heap[parent], max_heap[i]) = (max_heap[i], max_heap[parent])
            i = parent

    def _move_down(i: int) -> None:
        size = len(max_heap)
        while True:
            left = 2 * i + 1
            right = 2 * i + 2
            max_index = i
            if left < size and nums[max_heap[left]] > nums[max_heap[max_index]]:
                max_index = left
            if right < size and nums[max_heap[right]] > nums[max_heap[max_index]]:
                max_index = right
            if max_index == i:
                break
            (max_heap[i], max_heap[max_index]) = (max_heap[max_index], max_heap[i])
            i = max_index
    for num in nums:
        if len(max_heap) < k:
            _move_up(len(max_heap))
            max_heap.append(num)
            _move_down(len(max_heap) - 1)
        elif num < max_heap[0]:
            _move_up(len(max_heap))
            max_heap.append(num)
            _move_down(len(max_heap) - 1)
    return sorted(max_heap)

import numpy as np
def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function and its derivative.

    Args:
        x: A scalar, Python list, or NumPy array of real numbers.
        deriv: Whether to return the derivative (True) or the SoftPlus value (False).

    Returns:
        A list of floats if x is a scalar or list, or a single float if x is a NumPy array.
    """
    x = np.array(x)
    if x.ndim == 0:
        result = np.zeros(1)
        result[0] = softplus(x, deriv)
        return result

    def softplus_func(val):
        if val > 0:
            return np.log(1 + np.exp(val))
        else:
            return np.log(1 + np.exp(val))
    softplus_values = np.where(x > 0, softplus_func(x), np.log(1 + np.exp(x)))
    if deriv:
        sigmoid_values = np.where(x > 0, np.exp(x) / (1 + np.exp(x)), np.exp(x) / (1 + np.exp(x)))
        return sigmoid_values
    rounded_result = np.round(softplus_values, 4)
    if x.ndim == 0:
        return rounded_result[0]
    else:
        return rounded_result

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Args:
        corpus: List of word tokens
        sequence: List of word tokens
        N: Order of the N-gram model (≥1)

    Returns:
        float: Total log-probability of the sequence, rounded to 4 decimal places.
        If any required count is zero, returns float('-inf').

    Raises:
        ValueError: If N is less than 1
    """
    if N < 1:
        raise ValueError('N must be at least 1')
    if N == 1:
        counts = Counter(corpus)
    else:
        counts = {}
        for i in range(len(corpus) - N + 1):
            n_gram = ' '.join(corpus[i:i + N])
            counts[n_gram] = counts.get(n_gram, 0) + 1
    for i in range(len(sequence) - N + 1):
        n_gram = ' '.join(sequence[i:i + N])
        if N == 1:
            if n_gram not in counts or counts[n_gram] == 0:
                return float('-inf')
        elif n_gram not in counts or counts[n_gram] == 0:
            return float('-inf')
    total_log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        n_gram = ' '.join(sequence[i:i + N])
        if N == 1:
            prob = counts[n_gram] / len(corpus)
        else:
            prob = counts[n_gram] / counts.get(' '.join(sequence[i:i + N - 1]), 0)
        total_log_prob += np.log(prob)
    return round(total_log_prob, 4)

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if not y.size:
        return 0.0
    if criterion == 'entropy':
        probabilities = np.unique(y, return_counts=True)[1] / y.size
        entropy = -np.sum(probabilities * np.log2(probabilities))
        return round(entropy, 4)
    elif criterion == 'gini':
        probabilities = np.unique(y, return_counts=True)[1] / y.size
        gini = 1 - np.sum(probabilities * (1 - probabilities))
        return round(gini, 4)
    elif criterion == 'mse':
        mean = np.mean(y)
        squared_diffs = (y - mean) ** 2
        mse = np.mean(squared_diffs)
        return round(mse, 4)
    else:
        raise ValueError("Invalid criterion. Must be one of 'entropy', 'gini', or 'mse'.")

import numpy as np
from itertools import accumulate
def gen_hmm_data(S: list[float], A: list[list[float]], B: list[list[float]], n_sample: int, seed: int | None=None) -> tuple[list[int], list[int]]:
    """Generate a sequence of hidden states and observations from a Hidden Markov Model.

    The function validates the probability distributions, optionally sets a random
    seed for reproducibility, and then performs sequential sampling: initial state
    → observation → (transition → observation) repeated *n_sample*-1 more times.

    Args:
        S: Initial state distribution (length N).
        A: State-transition matrix (shape N×N).
        B: Emission probability matrix (shape N×M).
        n_sample: Total length of the desired sequence (must be ≥1).
        seed: Optional integer to seed NumPy’s RNG so that identical inputs
              return identical outputs.

    Returns:
        A tuple (states, observations) where both are lists of integers of
        length *n_sample*. If any probability distribution is invalid the
        function must return **-1**.
    """
    if not isinstance(S, list):
        return -1
    if len(S) != n_sample:
        return -1
    if not isinstance(A, list) or not all((isinstance(row, list) for row in A)):
        return -1
    if len(A) != len(S) or len(A[0]) != len(S):
        return -1
    if not isinstance(B, list) or not all((isinstance(row, list) for row in B)):
        return -1
    if len(B) != len(S) or any((len(row) != len(B[0]) for row in B)):
        return -1
    for row in S:
        if not np.isclose(np.sum(row), 1.0, atol=1e-08):
            return -1
    for i in range(len(A)):
        for j in range(len(A[i])):
            if not np.isclose(np.sum(A[i]), 1.0, atol=1e-08):
                return -1
    for i in range(len(B)):
        for k in range(len(B[i])):
            if not np.isclose(np.sum(B[i]), 1.0, atol=1e-08):
                return -1
    if seed is not None:
        np.random.seed(seed)
    states = [int(np.random.choice(range(len(S)), p=S))]
    observations = []
    for _ in range(n_sample - 1):
        current_state = states[-1]
        next_state = int(np.random.choice(range(len(A[current_state]))))
        observation = int(np.random.choice(range(len(B[current_state]))))
        states.append(next_state)
        observations.append(observation)
    return (states, observations)

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    m = len(B[0]) if B else 0
    if n != len(A) or len(A[0]) != n:
        return -1
    if m == 0 or len(observations) == 0:
        return -1
    for obs in observations:
        if obs < 0 or obs >= m:
            return -1
    alpha = [np.zeros(n) for _ in range(len(observations))]
    alpha[0] = S[:]
    for t in range(1, len(observations)):
        for i in range(n):
            prob = alpha[t - 1][i] * A[i][observations[t - 1]]
            for j in range(n):
                prob += alpha[t - 1][j] * A[j][observations[t - 1]]
            alpha[t][i] = prob
    total_prob = sum(alpha[-1])
    return round(total_prob, 4)

import numpy as np
def drelu(Z):
    """Return the element-wise derivative of the ReLU activation.

    Parameters
    ----------
    Z : int | float | list | numpy.ndarray
        Input data that can be a scalar, list (any depth), or ndarray.

    Returns
    -------
    float | list
        Derivative of ReLU with the same shape layout as *Z* (float if *Z* is a scalar).
    """
    if isinstance(Z, np.ndarray):
        Z = Z.copy()
    else:
        Z = np.array(Z)
    derivative = np.where(Z > 0, 1.0, 0.0)
    if isinstance(Z, np.ndarray) and Z.ndim == 0:
        return float(derivative.item())
    else:
        return derivative.tolist()

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, np.ndarray):
        return np.where(Z <= 0, 0, Z)
    elif isinstance(Z, list):
        return [max(0, x) for x in Z]
    elif isinstance(Z, (int, float)):
        return Z if Z >= 0 else 0
    else:
        return np.where(Z <= 0, 0, Z)

import numpy as np
def linear(z):
    """Linear (identity) activation function.

    Args:
        z: Scalar, list, tuple or NumPy ndarray.

    Returns:
        The same value(s) as *z*, obeying the rules:
            • Scalars are returned unchanged.
            • Non-scalar inputs are returned as a NumPy ndarray.
    """
    if isinstance(z, np.ndarray):
        return z
    elif isinstance(z, (list, tuple)):
        return np.array(z)
    else:
        return z

import numpy as np
def best_arm(payoff_probs: list[float]) -> tuple[float, int]:
    """Find the arm with the highest expected reward in a Bernoulli bandit.

    Parameters
    ----------
    payoff_probs : list[float]
        A list where each element is the success probability of the
        corresponding arm. Probabilities must lie in the interval [0, 1].

    Returns
    -------
    tuple
        (max_expected_reward, best_arm_index).  If the input list is empty or
        contains an invalid probability (<0 or >1), the function must return
        (-1.0, -1).
    """
    for p in payoff_probs:
        if not 0 <= p <= 1:
            return (-1.0, -1)
    if not payoff_probs:
        return (-1.0, -1)
    max_prob = max(payoff_probs)
    best_index = payoff_probs.index(max_prob)
    for i in range(len(payoff_probs)):
        if payoff_probs[i] == max_prob:
            best_index = i
            break
    return (max_prob, best_index)

import numpy as np
def activation_forward_backward(X: np.ndarray, activation: str, upstream_grad: np.ndarray) -> tuple[np.ndarray, np.ndarray]:
    """Apply an activation function and compute its local gradient.

    Parameters
    ----------
    X : np.ndarray
        Raw pre-activation values.
    activation : str
        Name of the activation function. Supported values are
        "sigmoid", "tanh", "relu", and "softmax".
    upstream_grad : np.ndarray
        Gradient that arrives from the layer above (must have the same shape as
        `X`).

    Returns
    -------
    tuple[np.ndarray, np.ndarray]
        A two-element tuple `(A, grad)` where:
          • `A`    – the activation applied to `X`.
          • `grad` – the element-wise product of `upstream_grad` and the local
                      derivative of the activation (rounded to 4 decimals).
    """
    if activation == 'sigmoid':
        A = sigmoid(X)
        grad = sigmoid_derivative(X) * upstream_grad
    elif activation == 'tanh':
        A = tanh(X)
        grad = tanh_derivative(X) * upstream_grad
    elif activation == 'relu':
        A = relu(X)
        grad = relu_derivative(X) * upstream_grad
    elif activation == 'softmax':
        A = softmax(X)
        grad = softmax_derivative(X) * upstream_grad
    else:
        raise ValueError(f'Unsupported activation function: {activation}')
    A = np.round(A, 4)
    grad = np.round(grad, 4)
    return (A, grad)
def sigmoid(X: np.ndarray) -> np.ndarray:
    return 1 / (1 + np.exp(-X))
def tanh(X: np.ndarray) -> np.ndarray:
    return (np.exp(X) - np.exp(-X)) / (np.exp(X) + np.exp(-X))
def relu(X: np.ndarray) -> np.ndarray:
    return np.where(X > 0, X, 0)
def softmax(X: np.ndarray) -> np.ndarray:
    exp_X = np.exp(X)
    sum_exp = np.sum(exp_X, axis=-1, keepdims=True)
    return exp_X / sum_exp
def sigmoid_derivative(X: np.ndarray) -> np.ndarray:
    return sigmoid(X) * (1 - sigmoid(X))
def tanh_derivative(X: np.ndarray) -> np.ndarray:
    return 1 - np.tanh(X) ** 2
def relu_derivative(X: np.ndarray) -> np.ndarray:
    return np.where(X > 0, 1, 0)

def accuracy_score(y_true, y_pred):
    """Return the classification accuracy between *y_true* and *y_pred*.

    The function must:
      • return ‑1 if the two containers have different lengths;
      • return 0 when both containers are empty;
      • otherwise return the proportion of matching elements rounded to
        4 decimal places.

    Args:
        y_true (list | tuple): Ground-truth class labels.
        y_pred (list | tuple): Predicted class labels.

    Returns:
        float | int: The accuracy or ‑1 according to the rules above.
    """
    if len(y_true) != len(y_pred):
        return -1
    if not y_true and (not y_pred):
        return 0
    matches = sum((1 for (a, b) in zip(y_true, y_pred) if a == b))
    accuracy = matches / len(y_true)
    return round(accuracy, 4)

import numpy as np
def tanh_grad(z):
    """Derivative of the hyper-bolic tangent (tanh) activation.

    The derivative is computed element-wise as 1 - tanh(z)**2.

    Args:
        z: A scalar, Python list or NumPy ndarray of floats/ints.

    Returns:
        float | list[float]: If `z` is scalar a float is returned, otherwise a
        Python list with each element rounded to 4 decimals.
    """
    if isinstance(z, np.ndarray):
        grad = 1 - np.tanh(z) ** 2
        return grad.tolist()
    else:
        grad = 1 - np.tanh(z) ** 2
        return round(grad, 4)

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """
    Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini_impurity(labels):
        """Compute Gini impurity of a class label vector."""
        (unique, counts) = np.unique(labels, return_counts=True)
        probabilities = counts / len(labels)
        return 1 - np.sum(probabilities ** 2)

    def split_data(X, y, feature_idx, threshold):
        """Split data into left and right child based on threshold."""
        left = []
        right = []
        for (i, (x, y_val)) in enumerate(zip(X, y)):
            if x < threshold:
                left.append((x, y_val))
            else:
                right.append((x, y_val))
        return (left, right)

    def build_tree(X, y, depth=0, max_depth=None):
        """Build a decision tree recursively."""
        if (np.all(y == y[0]) or (max_depth is not None and depth >= max_depth)) or len(X) == 0:
            return np.array([y[0]], dtype=int)
        best_gain = -1
        best_split = None
        for feature_idx in range(X.shape[1]):
            unique_vals = np.unique(X[:, feature_idx])
            if len(unique_vals) == 1:
                continue
            for i in range(1, len(unique_vals)):
                threshold = (unique_vals[i - 1] + unique_vals[i]) / 2
                (left, right) = split_data(X, y, feature_idx, threshold)
                parent_impurity = gini_impurity(y)
                left_impurity = gini_impurity(left[1])
                right_impurity = gini_impurity(right[1])
                gain = parent_impurity - (left_impurity * len(left) + right_impurity * len(right)) / len(y)
                if gain > best_gain:
                    best_gain = gain
                    best_split = (feature_idx, threshold, left, right)
        if best_gain <= 0:
            return np.array([y[0]], dtype=int)
        left_child = build_tree(np.array(left), y, depth + 1, max_depth)
        right_child = build_tree(np.array(right), y, depth + 1, max_depth)
        return np.array(([left_child[i], right_child[i]] for i in range(len(left_child))))
    root = build_tree(X_train, y_train, depth=0, max_depth=max_depth)
    predictions = []
    for x in X_test:
        path = [x]
        current_node = root
        while True:
            (feature_idx, threshold, left, right) = current_node
            if not left and (not right):
                predictions.append(current_node[0])
                break
            if path[-1] < threshold:
                current_node = left
            else:
                current_node = right
            path.append(current_node)
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def euclidean(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        if len(x) != len(y) or len(x) == 0 or len(y) == 0:
            return -1
        if not all((isinstance(v, (int, float)) for v in x)) or not all((isinstance(v, (int, float)) for v in y)):
            return -1
        squared_diffs = [(x_i - y_i) ** 2 for (x_i, y_i) in zip(x, y)]
        return round(np.sqrt(np.sum(squared_diffs)), 4)

    def manhattan(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        if len(x) != len(y) or len(x) == 0 or len(y) == 0:
            return -1
        if not all((isinstance(v, (int, float)) for v in x)) or not all((isinstance(v, (int, float)) for v in y)):
            return -1
        return round(np.sum(abs(x - y)), 4)

    def chebyshev(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        if len(x) != len(y) or len(x) == 0 or len(y) == 0:
            return -1
        if not all((isinstance(v, (int, float)) for v in x)) or not all((isinstance(v, (int, float)) for v in y)):
            return -1
        return round(max((abs(x[i] - y[i]) for i in range(len(x)))), 4)

    def cosine(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        if len(x) != len(y) or len(x) == 0 or len(y) == 0:
            return -1
        if not all((isinstance(v, (int, float)) for v in x)) or not all((isinstance(v, (int, float)) for v in y)):
            return -1
        if np.linalg.norm(x) == 0 or np.linalg.norm(y) == 0:
            return -1
        dot_product = np.dot(x, y)
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        return round(1 - dot_product / (norm_x * norm_y), 4)
    if name not in ['euclidean', 'manhattan', 'chebyshev', 'cosine']:
        return -1
    if name == 'euclidean':
        return euclidean
    elif name == 'manhattan':
        return manhattan
    elif name == 'chebyshev':
        return chebyshev
    elif name == 'cosine':
        return cosine

