def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        return round(z / (1 + abs(z)), 4)
    elif isinstance(z, np.ndarray):
        return np.round(z / (1 + np.abs(z)), 4).tolist()
    else:
        return []

import numpy as np
def build_alias_table(probs: list[float] | np.ndarray) -> tuple[list[float], list[int]]:
    """
    Builds the alias table for the alias sampling method.

    Args:
        probs: A 1-D list or NumPy array of positive floats summing to 1.

    Returns:
        A tuple (prob_table, alias_table) where:
            * prob_table is a list of floats rounded to 4 decimals.
            * alias_table is a list of integers.
        Returns -1 if input is invalid.
    """
    if not isinstance(probs, np.ndarray):
        probs = np.array(probs)
    if probs.ndim != 1 or probs.size != probs.shape[0]:
        return -1
    if np.any(probs < 0) or not np.isclose(np.sum(probs), 1.0, atol=1e-12):
        return -1
    N = probs.shape[0]
    scaled_probs = N * probs
    small = []
    large = []
    for i in range(N):
        if scaled_probs[i] < 1:
            small.append(i)
        else:
            large.append(i)
    while small and large:
        l = small.pop()
        g = large.pop()
        alias_table = [0] * N
        prob_table = [0.0] * N
        alias_table[l] = g
        prob_table[l] = scaled_probs[l]
        scaled_probs[g] += scaled_probs[l] - 1
        if scaled_probs[g] < 1:
            small.append(g)
        else:
            large.append(g)
    for i in range(N):
        if i in small:
            prob_table[i] = 1.0
        elif i in large:
            prob_table[i] = 1.0
        else:
            prob_table[i] = 0.0
    prob_table = [round(p, 4) for p in prob_table]
    alias_table = [int(x) for x in alias_table]
    return (prob_table, alias_table)

import functools
from typing import Callable, Any
def doublewrap(decorator_func: Callable[..., Callable[..., Any]]) -> Callable[..., Any]:
    """Return a decorator that can be used *with* or *without* parentheses.

    Args:
        decorator_func: A callable that expects the function to decorate as
            its first positional argument followed only by *optional*
            positional or keyword arguments.

    Returns:
        A new decorator that forwards the call in the correct form so that
        ``decorator_func`` can be applied either directly or after receiving
        its own optional parameters.
    """

    @functools.wraps(decorator_func)
    def wrapper(*args, **kwargs):
        if not args and (not kwargs):
            return decorator_func(*args, **kwargs)
        return decorator_func(*args, **kwargs)
    return wrapper

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand):
        score = sum(hand)
        aces = sum((1 for card in hand if card == 1))
        if aces > 0 and score + 10 <= 21:
            score += 10
        return score
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    if player_score > 21:
        return -1
    if dealer_score > 21:
        return 1
    if player_score == 21 and len(player) == 2:
        return 1.5
    if dealer_score == 21 and len(dealer) == 2:
        return -1
    if player_score > dealer_score:
        return 1
    elif player_score < dealer_score:
        return -1
    else:
        return 0

import numpy as np
def squared_log_error(actual, predicted):
    """Compute element-wise squared logarithmic errors.

    Parameters:
    actual (np.ndarray): Actual values (non-negative)
    predicted (np.ndarray): Predicted values (non-negative)

    Returns:
    np.ndarray: Element-wise squared log errors
    """
    if not (isinstance(actual, (list, tuple, np.ndarray)) and isinstance(predicted, (list, tuple, np.ndarray))):
        return -1
    if actual.shape != predicted.shape:
        return -1
    if not np.all(np.isclose(actual, actual, atol=1e-10)):
        return -1
    if not np.all(np.geq(actual, 0)):
        return -1
    if not np.all(np.geq(predicted, 0)):
        return -1
    return np.square(np.log1p(actual) - np.log1p(predicted))
def mean_squared_log_error(actual, predicted):
    """Calculate Mean Squared Logarithmic Error (MSLE).

    Parameters:
    actual (list | tuple | np.ndarray): Sequence of true values.  All elements must be non-negative.
    predicted (list | tuple | np.ndarray): Sequence of predicted values.  Must be the same length as
                                          `actual` and contain only non-negative numbers.

    Returns:
    float: The MSLE rounded to 4 decimals.  If the inputs are invalid the
          function returns -1.
    """
    if not (isinstance(actual, (list, tuple, np.ndarray)) and isinstance(predicted, (list, tuple, np.ndarray))):
        return -1
    if actual.shape != predicted.shape:
        return -1
    if not np.all(np.isclose(actual, actual, atol=1e-10)):
        return -1
    if not np.all(np.geq(actual, 0)):
        return -1
    if not np.all(np.geq(predicted, 0)):
        return -1
    squared_errors = squared_log_error(actual, predicted)
    return round(np.mean(squared_errors), 4)

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """
    if isinstance(z, np.ndarray):
        return np.round(1 / (1 + np.exp(-z)), 4).tolist()
    elif isinstance(z, list):
        return [round(1 / (1 + np.exp(-x)), 4) for x in z]
    else:
        return round(1 / (1 + np.exp(-z)), 4)

