def create_encoding_dicts(obs_ids: list, action_ids: list, obs_encoder: dict | None=None, act_encoder: dict | None=None) -> dict:
    """
    Generate forward and backward mappings for observations and actions.

    Args:
        obs_ids (list): All distinct observation identifiers.
        action_ids (list): All distinct action identifiers.
        obs_encoder (dict | None, optional): Existing obs→number mapping.
        act_encoder (dict | None, optional): Existing act→number mapping.

    Returns:
        dict: A dictionary that contains the keys 'obs2num', 'num2obs',
        'act2num', and 'num2act'.
    """
    obs2num = {}
    num2obs = {}
    act2num = {}
    num2act = {}
    if obs_encoder is None:
        for (i, obs) in enumerate(obs_ids):
            obs2num[obs] = i
            num2obs[i] = obs
    else:
        for (key, value) in obs_encoder.items():
            obs2num[key] = value
            num2obs[value] = key
    if act_encoder is None:
        for (i, act) in enumerate(action_ids):
            act2num[act] = i
            num2act[i] = act
    else:
        for (key, value) in act_encoder.items():
            act2num[key] = value
            num2act[value] = key
    return {'obs2num': obs2num, 'num2obs': num2obs, 'act2num': act2num, 'num2act': num2act}

import numpy as np
def sigmoid(x):
    """Element-wise logistic sigmoid."""
    return 1 / (1 + np.exp(-x))
def tanh(x):
    """Element-wise hyperbolic tangent."""
    return (np.exp(x) - np.exp(-x)) / (np.exp(x) + np.exp(-x))
def relu(x):
    """Element-wise Rectified Linear Unit."""
    return np.where(x > 0, x, 0)
def leaky_relu(x, alpha: float=0.01):
    """Element-wise Leaky-ReLU with slope alpha for x < 0."""
    return np.where(x < 0, alpha * x, x)
def softmax(x):
    """Numerically stable softmax over a 1-D input."""
    exp_x = np.exp(x)
    sum_exp = np.sum(exp_x, axis=0)
    return exp_x / sum_exp
def get_activation(name):
    """Return the activation function by name.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If name is not recognised.
    """
    if name == 'sigmoid':
        return sigmoid
    elif name == 'tanh':
        return tanh
    elif name == 'relu':
        return relu
    elif name == 'leaky_relu':
        return leaky_relu
    elif name == 'softmax':
        return softmax
    else:
        raise ValueError('Invalid activation function.')

def king_loss_fn(x: int | float) -> float:
    """Compute King’s piece-wise loss for a scalar value.

    The loss function L(x) is defined by four segments:
      • x ≤ 250:   L(x) = –0.25 * x + 82.50372665317208
      • 250 < x ≤ 600: L(x) = 20.00372665317208
      • 600 < x ≤ 700: L(x) = –0.2 * x + 140.00372665317207
      • x > 700:  L(x) = 0.003726653172066108

    The result must be rounded to 4 decimal places.

    Args:
        x: An integer or float input.

    Returns:
        A float rounded to four decimal places.
    """
    if x <= 250:
        return round(-0.25 * x + 82.50372665317208, 4)
    elif 250 < x <= 600:
        return round(20.00372665317208, 4)
    elif 600 < x <= 700:
        return round(-0.2 * x + 140.00372665317207, 4)
    else:
        return round(0.003726653172066108, 4)

import math
import string
from collections import Counter
def additive_ngram_log_prob(corpus: list[str], sentence: str, N: int, k: float=1.0, unk: bool=True) -> float:
    """
    Compute the base-10 logarithm of the probability of a target sentence under an additive-smoothed N-gram model.

    Args:
        corpus: List of training sentences.
        sentence: The sentence to compute the probability for.
        N: Order of the model (1 for unigram, 2 for bigram, etc.).
        k: Additive smoothing parameter (default 1.0).
        unk: If True, unseen tokens are mapped to a special <unk> symbol.

    Returns:
        A single float, rounded to 4 decimal places, representing the base-10 logarithm of the sentence probability.
    """

    def tokenize_and_pad(text):
        text = text.lower()
        tokens = text.split()
        processed_tokens = []
        for token in tokens:
            cleaned_token = token
            while cleaned_token and cleaned_token[0] in string.punctuation:
                cleaned_token = cleaned_token[1:]
            while cleaned_token and cleaned_token[-1] in string.punctuation:
                cleaned_token = cleaned_token[:-1]
            processed_tokens.append(cleaned_token)
        padded_tokens = ['<bol>'] + processed_tokens + ['<eol>']
        return padded_tokens
    padded_sentence = tokenize_and_pad(sentence)

    def count_ngrams(tokens, N):
        if N == 1:
            return Counter(tokens)
        elif N == 2:
            return Counter(((tokens[i], tokens[i + 1]) for i in range(len(tokens) - 1)))
        elif N == 3:
            return Counter(((tokens[i], tokens[i + 1], tokens[i + 2]) for i in range(len(tokens) - 2)))
        else:
            raise ValueError(f'Unsupported N value: {N}')
    ngram_counts = count_ngrams(padded_sentence, N)
    total_tokens = sum((len(tokens) for tokens in padded_sentence))
    vocab_size = len(ngram_counts)
    if unk:
        vocab_size += 1
        ngram_counts['<unk>'] = 0
    log_prob = 0.0
    for i in range(len(padded_sentence) - N + 1):
        history = padded_sentence[i:i + N]
        word = padded_sentence[i + N]
        count = ngram_counts.get(tuple(history), 0)
        numerator = count + k
        denominator = count + k * vocab_size
        log_prob += math.log10(numerator / denominator)
    return round(log_prob, 4)

import numpy as np
def L1Regularization(alpha: float):
    """Create a callable object that implements L1 regularisation.

    The returned object must support two operations:
    1. call with a NumPy array to obtain the L1 penalty value
    2. call its .grad(w) method to obtain the gradient of the penalty

    Args:
        alpha: Non-negative float controlling the strength of the regularisation.

    Returns:
        An instance that fulfils the described interface.
    """

    class L1RegularizationImpl:

        def __init__(self, alpha):
            self.alpha = alpha

        def __call__(self, w):
            """Return the L1 penalty value."""
            return self.alpha * np.abs(w).sum()

        def grad(self, w):
            """Return the gradient of the L1 penalty with respect to w."""
            sign = np.where(w > 0, 1, np.where(w == 0, 0, -1))
            return self.alpha * sign
    return L1RegularizationImpl(alpha)

import numpy as np
def squared_error(actual, predicted):
    """Calculate the squared error or mean squared error.

    If *actual* and *predicted* are scalars, return the squared difference.
    If they are one-dimensional iterables (same length), return the mean squared
    error.  Return -1 for length mismatches or empty iterables.

    All results must be rounded to 4 decimal places.
    """
    if isinstance(actual, (int, float)) and isinstance(predicted, (int, float)):
        return round((actual - predicted) ** 2, 4)
    if isinstance(actual, (list, tuple, np.ndarray)) and isinstance(predicted, (list, tuple, np.ndarray)):
        if len(actual) != len(predicted):
            return -1
        if len(actual) == 0:
            return -1
        squared_diffs = [(a - p) ** 2 for (a, p) in zip(actual, predicted)]
        return round(np.mean(squared_diffs), 4)
    if isinstance(actual, (list, tuple, np.ndarray)) and (not isinstance(predicted, (int, float))):
        return -1
    if not isinstance(actual, (list, tuple, np.ndarray)) and isinstance(predicted, (int, float)):
        return -1
    return -1

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value)
    if isinstance(tree_a, Leaf) or isinstance(tree_b, Leaf):
        return False
    if not (isinstance(tree_a, Node) and isinstance(tree_b, Node)):
        return False
    if abs(tree_a.feature - tree_b.feature) > 1e-08 or abs(tree_a.threshold - tree_b.threshold) > 1e-08:
        return False
    return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)

import heapq
from typing import List, Union
Number = Union[int, float]
def top_k_elements(sequence: List[Number], k: int, order: str='largest') -> List[Number]:
    """Keeps only the k largest or k smallest numbers from *sequence*.

    The function must visit each element exactly once and store no more than
    *k* items at any time by making use of the heapq module (binary heap).

    Args:
        sequence: A list of integers or floats representing the data stream.
        k: The number of extreme values to keep.
        order: "largest" to retain the k biggest values, "smallest" to retain
            the k smallest.

    Returns:
        A list with at most k numbers sorted according to *order*.
    """
    if k <= 0 or not sequence:
        return []
    if k >= len(sequence):
        if order == 'largest':
            return sorted(sequence, reverse=True)
        else:
            return sorted(sequence)
    min_heap = []
    result = []
    for num in sequence:
        if len(min_heap) < k:
            heapq.heappush(min_heap, num)
        elif num > min_heap[0]:
            heapq.heappop(min_heap)
            heapq.heappush(min_heap, num)
    if order == 'largest':
        result = sorted(min_heap, reverse=True)
    else:
        result = sorted(min_heap)
    return result

def update_kernel_settings(parameters: dict, hyperparameters: dict, summary_dict: dict) -> tuple[dict, dict]:
    """Update kernel parameters and hyperparameters.

    The function must not modify the input dictionaries.  It should return two
    *new* dictionaries in which the supplied `summary_dict` updates have been
    applied following the rules explained in the task description.

    Args:
        parameters (dict):      Existing kernel parameters.
        hyperparameters (dict): Existing kernel hyperparameters.
        summary_dict (dict):    Dictionary that may contain the keys
            "parameters" and/or "hyperparameters" whose values are dictionaries
            of updates.  Any top-level keys are also treated as updates.

    Returns:
        tuple[dict, dict]: `(new_parameters, new_hyperparameters)` – the
        updated dictionaries.
    """
    new_parameters = dict(parameters)
    new_hyperparameters = dict(hyperparameters)
    if 'parameters' in summary_dict:
        for (key, value) in summary_dict['parameters'].items():
            if key in new_parameters:
                new_parameters[key] = value
            else:
                new_parameters[key] = value
    if 'hyperparameters' in summary_dict:
        for (key, value) in summary_dict['hyperparameters'].items():
            if key in new_hyperparameters:
                new_hyperparameters[key] = value
            else:
                new_hyperparameters[key] = value
    for (key, value) in summary_dict.items():
        if key not in ['parameters', 'hyperparameters']:
            if key in new_parameters:
                new_parameters[key] = value
            else:
                new_parameters[key] = value
    return (new_parameters, new_hyperparameters)

import math
def noam_learning_rate(step: int, model_dim: int=512, scale_factor: float=1.0, warmup_steps: int=4000) -> float:
    """Return the learning-rate given by the Noam schedule.

    The function must follow the formula described in the task description and
    round the result to 9 decimal places.
    """
    if step < 1:
        return 0.0
    if step <= warmup_steps:
        lr = scale_factor * model_dim ** (-0.5) * step ** (-0.5)
    else:
        lr = scale_factor * model_dim ** (-0.5) * step ** (-0.5) * warmup_steps ** (-1.5)
    return round(lr, 9)

import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    if name == 'zeros':

        def zeros_initializer(shape, scale=1.0):
            return np.zeros(shape, dtype=np.float32)
        return zeros_initializer
    elif name == 'ones':

        def ones_initializer(shape, scale=1.0):
            return np.ones(shape, dtype=np.float32) * scale
        return ones_initializer
    elif name == 'normal':

        def normal_initializer(shape, scale=1.0):
            return np.random.normal(0, scale, shape)
        return normal_initializer
    elif name == 'glorot_uniform':

        def glorot_uniform_initializer(shape, scale=1.0):
            fan_in = shape[0]
            fan_out = shape[1]
            if len(shape) == 2:
                fan_in = shape[0]
                fan_out = shape[1]
            else:
                raise ValueError('Glorot uniform initialization is only supported for 2D shapes.')
            limit = scale * np.sqrt(6 / (fan_in + fan_out))
            return np.random.uniform(-limit, limit, shape)
        return glorot_uniform_initializer
    else:
        raise ValueError('Unsupported initialisation strategy: {}'.format(name))

import heapq
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.

    Args:
        tokens: A list of strings representing the input tokens.

    Returns:
        A dictionary mapping each unique token to its Huffman code.
    """
    if not tokens:
        return {}
    frequency = {}
    for token in tokens:
        frequency[token] = frequency.get(token, 0) + 1
    heap = []
    for (token, count) in frequency.items():
        heapq.heappush(heap, (count, token, ''))
    while len(heap) > 1:
        (freq1, token1, _) = heapq.heappop(heap)
        (freq2, token2, _) = heapq.heappop(heap)
        combined_freq = freq1 + freq2
        parent_token = f'({token1}, {token2})'
        heapq.heappush(heap, (combined_freq, parent_token, ''))
    code_dict = {}
    current_node = heap[0]
    stack = [(current_node, '')]
    while stack:
        (node, path) = stack.pop()
        if node == '':
            continue
        if node.split('(')[1] == '':
            code_dict[node] = path
        else:
            children = node.split('(')[1].split(')')[0].split(',')
            for child in children:
                child_token = child.strip()
                child_node = f'({child_token}, {node})'
                new_path = path + '0' if child_token == 'left' else '1'
                stack.append((child_node, new_path))
    return code_dict

import re
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]):
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    if isinstance(param, str):
        match = re.match('^(\\w+)(\\((.*?))?\\s*$', param)
        if not match:
            raise ValueError(f'Invalid kernel description: {param}')
        kernel_type = match.group(1).lower()
        args = match.group(3) if match.group(3) else ''
        if kernel_type not in ['linear', 'polynomial', 'rbf']:
            raise NotImplementedError(f'Unsupported kernel type: {kernel_type}')
        try:
            if kernel_type == 'linear':
                return LinearKernel()
            elif kernel_type == 'polynomial':
                (degree_str, coef0_str) = args.split(',') if ',' in args else (args, '')
                degree = int(degree_str)
                coef0 = float(coef0_str)
                return PolynomialKernel(degree=degree, coef0=coef0)
            elif kernel_type == 'rbf':
                gamma_str = args.split('(')[1].split(')')[0]
                gamma = float(gamma_str)
                return RBFKernel(gamma=gamma)
        except (ValueError, IndexError):
            raise ValueError(f'Invalid kernel description: {param}')
    if isinstance(param, dict):
        if 'hyperparameters' not in param or not isinstance(param['hyperparameters'], dict):
            raise ValueError("Dictionary must have 'hyperparameters' key")
        hyperparameters = param['hyperparameters']
        if 'id' not in hyperparameters or hyperparameters['id'] not in ['LinearKernel', 'PolynomialKernel', 'RBFKernel']:
            raise ValueError('Invalid kernel type in hyperparameters')
        if 'hyperparameters' in hyperparameters:
            del hyperparameters['hyperparameters']
        if not hyperparameters:
            return KernelBase()
        if hyperparameters['id'] == 'LinearKernel':
            return LinearKernel()
        elif hyperparameters['id'] == 'PolynomialKernel':
            (degree_str, coef0_str) = hyperparameters['hyperparameters'].split(',') if ',' in hyperparameters['hyperparameters'] else (hyperparameters['hyperparameters'], '')
            degree = int(degree_str)
            coef0 = float(coef0_str)
            return PolynomialKernel(degree=degree, coef0=coef0)
        elif hyperparameters['id'] == 'RBFKernel':
            gamma_str = hyperparameters['hyperparameters'].split('(')[1].split(')')[0]
            gamma = float(gamma_str)
            return RBFKernel(gamma=gamma)
    raise NotImplementedError('Unsupported kernel representation')

import numpy as np
def selu(x, order: int=0):
    """Compute the SELU activation or its derivatives.

    Parameters
    ----------
    x : float | list | np.ndarray
        Input value(s). Can be a scalar, 1-D list/array, or multi-D list/array.
    order : int, default 0
        0 → SELU(x)
        1 → first derivative d(SELU)/dx
        2 → second derivative d²(SELU)/dx²

    Returns
    -------
    list | float
        A Python list (or scalar for scalar input) containing the element-wise
        result, rounded to 4 decimals.
    """
    alpha = 1.6732632423543772
    scale = 1.0507009873554805
    if order == 0:

        def elu(x, alpha):
            return x if x > 0 else alpha * (np.exp(x) - 1)
        return scale * elu(x, alpha)
    elif order == 1:

        def d_selu_dx(x, alpha, scale):
            return scale * (1 if x > 0 else alpha * np.exp(x))
        return d_selu_dx(x, alpha, scale)
    elif order == 2:

        def d2_selu_dx2(x, alpha, scale):
            return scale * (0 if x > 0 else alpha * np.exp(x) * np.exp(x))
        return d2_selu_dx2(x, alpha, scale)
    else:
        raise ValueError('order must be 0, 1, or 2')

import numpy as np
import re
def apply_activation(x, param=None):
    """Apply a chosen activation to the input vector.

    Args:
        x (list[float] | np.ndarray): Input data. Anything that can be
            converted to a NumPy array of floats is accepted.
        param (str | None, optional): Description of the activation. See the
            task specification for the accepted values. If *None* the
            identity function is applied.

    Returns:
        list[float]: Result of applying the activation element-wise, rounded
            to six decimals and converted to a standard Python *list*.
    """
    x_array = np.array(x)
    if param is None:
        return np.round(x_array, 6).tolist()
    if param not in ['identity', 'relu', 'tanh', 'sigmoid', 'leaky_relu', 'affine']:
        raise ValueError(f'Unknown activation: {param}')
    if param == 'identity':
        return np.round(x_array, 6).tolist()
    elif param == 'relu':
        return np.round(np.maximum(x_array, 0), 6).tolist()
    elif param == 'tanh':
        return np.round(np.tanh(x_array), 6).tolist()
    elif param == 'sigmoid':
        return np.round(1 / (1 + np.exp(-x_array)), 6).tolist()
    elif param == 'leaky_relu':
        match = re.match('leaky_relu\\((\\d+\\.\\d+)?\\)', param)
        if match:
            alpha = float(match.group(1)) if match.group(1) else 0.01
        else:
            alpha = 0.01
        return np.round(np.where(x_array > 0, x_array, alpha * x_array), 6).tolist()
    elif param == 'affine':
        match = re.match('affine\\s*\\(\\s*(\\d+\\.\\d+)?\\s*,\\s*(\\d+\\.\\d+)?\\s*\\)', param)
        if match:
            slope = float(match.group(1)) if match.group(1) else 1.0
            intercept = float(match.group(2)) if match.group(2) else 0.0
        else:
            slope = 1.0
            intercept = 0.0
        return np.round(slope * x_array + intercept, 6).tolist()

import re
def parse_activation(act_str: str):
    """Parse an activation-function description string.

    The string can be one of the following forms:
        • "ReLU"
        • "Sigmoid"
        • "Tanh"
        • "Affine(slope=<number>, intercept=<number>)"
        • "Leaky ReLU(alpha=<number>)"

    Args:
        act_str: Description of the activation function.

    Returns:
        A dictionary with key "type" and, when applicable, its parameters.
        If the input format is invalid, return -1.
    """
    if act_str == 'ReLU':
        return {'type': 'ReLU'}
    elif act_str == 'Sigmoid':
        return {'type': 'Sigmoid'}
    elif act_str == 'Tanh':
        return {'type': 'Tanh'}
    elif act_str == 'Affine(slope=<number>, intercept=<number>)':
        match = re.match('Affine\\s*\\(slope=(\\d+\\.?\\d*),\\s*intercept=(\\d+\\.?\\d*)\\)', act_str)
        if not match:
            return -1
        slope = float(match.group(1))
        intercept = float(match.group(2))
        return {'type': 'Affine', 'slope': slope, 'intercept': intercept}
    elif act_str == 'Leaky ReLU(alpha=<number>)':
        match = re.match('Leaky ReLU\\s*\\(\\alpha=(\\d+\\.?\\d*)\\)', act_str)
        if not match:
            return -1
        alpha = float(match.group(1))
        return {'type': 'Leaky ReLU', 'alpha': alpha}
    else:
        return -1

def get_metric(name: str):
    """Return an evaluation metric function by *name*.

    Supported names:
    - 'accuracy'
    - 'precision'
    - 'recall'
    - 'f1'

    If the name is invalid, the function should return -1.
    The returned metric functions must accept two equal-length iterables
    (y_true, y_pred) containing only 0s and 1s and return the metric value
    rounded to 4 decimal places.
    """
    if name == 'accuracy':

        def accuracy(y_true, y_pred):
            tp = sum((y_true == y_pred for (y_true, y_pred) in zip(y_true, y_pred)))
            total = len(y_true)
            return round(tp / total, 4)
        return accuracy
    elif name == 'precision':

        def precision(y_true, y_pred):
            tp = sum((y_true == y_pred for (y_true, y_pred) in zip(y_true, y_pred)))
            fp = sum((1 for (y_true, y_pred) in zip(y_true, y_pred) if y_true != y_pred and y_pred == 1))
            if fp == 0:
                return 0.0
            return round(tp / (tp + fp), 4)
        return precision
    elif name == 'recall':

        def recall(y_true, y_pred):
            tp = sum((y_true == y_pred for (y_true, y_pred) in zip(y_true, y_pred)))
            fn = sum((1 for (y_true, y_pred) in zip(y_true, y_pred) if y_true != y_pred and y_pred == 0))
            if fn == 0:
                return 0.0
            return round(tp / (tp + fn), 4)
        return recall
    elif name == 'f1':

        def f1(y_true, y_pred):
            tp = sum((y_true == y_pred for (y_true, y_pred) in zip(y_true, y_pred)))
            fp = sum((1 for (y_true, y_pred) in zip(y_true, y_pred) if y_true != y_pred and y_pred == 1))
            fn = sum((1 for (y_true, y_pred) in zip(y_true, y_pred) if y_true != y_pred and y_pred == 0))
            if fp == 0 and fn == 0:
                return 0.0
            prec = tp / (tp + fp) if tp + fp != 0 else 0.0
            rec = tp / (tp + fn) if tp + fn != 0 else 0.0
            if prec == 0 and rec == 0:
                return 0.0
            return round(2 * prec * rec / (prec + rec), 4)
        return f1
    else:
        return -1

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Compute the Root Mean Squared Logarithmic Error (RMSLE) between actual and predicted values."""
    if len(actual) != len(predicted):
        return -1
    for (a, p) in zip(actual, predicted):
        if p < 0 or a < 0:
            return -1
    n = len(actual)
    log_plus_actual = np.log(1 + actual)
    log_plus_predicted = np.log(1 + predicted)
    squared_diff = np.square(log_plus_predicted - log_plus_actual)
    mean_squared_diff = np.mean(squared_diff)
    rmsle = np.sqrt(mean_squared_diff)
    return round(rmsle, 4)

from typing import List, Dict, Set, Tuple
def topological_sort(vertices: list, edges: list[Tuple]) -> list | None:
    """Determine a topological ordering of a directed graph.

    The implementation must use depth-first search (DFS) and follow the deterministic exploration rule described in the task description.

    Args:
        vertices: List of vertex identifiers (hashable).
        edges:    List of directed edges given as 2-tuples (source, target).

    Returns:
        • List of vertices in topological order when the graph is acyclic.
        • ``None`` when the graph contains at least one directed cycle.
    """
    adj = {vertex: [] for vertex in vertices}
    in_degree = {vertex: 0 for vertex in vertices}
    for (source, target) in edges:
        adj[source].append(target)
        in_degree[target] += 1
    queue = []
    for vertex in vertices:
        if in_degree[vertex] == 0:
            queue.append(vertex)
    topological_order = []
    while queue:
        vertex = queue.pop(0)
        topological_order.append(vertex)
        for neighbor in adj[vertex]:
            in_degree[neighbor] -= 1
            if in_degree[neighbor] == 0:
                queue.append(neighbor)
    if len(topological_order) == len(vertices):
        return topological_order
    else:
        return None

import numpy as np
def softmax(logits: np.ndarray) -> list[list[float]]:
    """Compute a row-wise numerically stable softmax."""
    if logits.ndim == 1:
        logits = logits.reshape(1, -1)
    exp_logits = np.exp(logits - np.max(logits, axis=1, keepdims=True))
    sum_exp = np.sum(exp_logits, axis=1, keepdims=True)
    softmax_result = exp_logits / sum_exp
    rounded_softmax = np.round(softmax_result, 6)
    return rounded_softmax.tolist()

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in (0, 1, 2):
        return -1
    if isinstance(x, (list, np.ndarray)):
        x = np.array(x)
    if isinstance(x, (float, int)):
        if order == 0:
            return round(1 / (1 + np.exp(-x)), 4)
        elif order == 1:
            return round(1 / (1 + np.exp(-x)) * (1 - 1 / (1 + np.exp(-x))), 4)

import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    transactions_set = set((tuple(transaction) for transaction in transactions))
    one_itemsets = []
    for item in transactions_set:
        count = sum((1 for transaction in transactions if item in transaction))
        if count / len(transactions) >= min_sup:
            one_itemsets.append((item,))
    two_itemsets = []
    for (item1, item2) in itertools.combinations(one_itemsets, 2):
        candidate = (item1, item2)
        if is_subset_frequent(candidate, one_itemsets):
            two_itemsets.append(candidate)
    two_itemset_support = {}
    for candidate in two_itemsets:
        support = sum((1 for transaction in transactions if candidate in transaction))
        two_itemset_support[candidate] = support
    frequent_two_itemsets = [(item1, item2) for ((item1, item2), support) in two_itemset_support.items() if support / len(transactions) >= min_sup]
    while True:
        current_itemset_size = len(frequent_two_itemsets)
        if current_itemset_size == 0:
            break
        next_itemsets = []
        for itemset in frequent_two_itemsets:
            for item in transactions_set:
                if item not in itemset:
                    new_itemset = itemset + (item,)
                    if is_subset_frequent(new_itemset, frequent_two_itemsets):
                        next_itemsets.append(new_itemset)
        new_itemsets = [itemset for itemset in next_itemsets if itemset not in frequent_two_itemsets]
        if not new_itemsets:
            break
        frequent_two_itemsets = [itemset for itemset in frequent_two_itemsets if itemset not in new_itemsets]
        frequent_two_itemsets.extend(new_itemsets)
    frequent_itemsets = sorted(frequent_two_itemsets, key=lambda x: (len(x), x))
    return frequent_itemsets
def is_subset_frequent(candidate: tuple, frequent_itemsets: list) -> bool:
    """Check if a candidate itemset is frequent based on its subsets."""
    for itemset in itertools.combinations(candidate, 1):
        if itemset not in frequent_itemsets:
            return False
    return True

import math
import heapq
from collections import Counter
from typing import Optional, List, Tuple
import numpy as np
def knn_kdtree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int) -> list[int]:
    """Classify *X_test* using the k-nearest neighbours algorithm with a KD-Tree.

    Parameters
    ----------
    X_train : np.ndarray
        Training samples of shape (n_samples, n_features).
    y_train : np.ndarray | list
        Integer class labels corresponding to *X_train*.
    X_test : np.ndarray
        Samples that need to be classified, shape (m_samples, n_features).
    k : int
        Number of neighbours that will vote for the class.

    Returns
    -------
    list[int]
        A list containing one predicted label per row of *X_test*.
    """
    if X_train.shape[0] == 0 or k <= 0:
        return []
    y_train = np.array(y_train)

    class KDTreeNode:

        def __init__(self, point, left, right):
            self.point = point
            self.left = left
            self.right = right
            self.height = 1

        def get_kd_tree(self):
            return self

    def build_kd_tree(points, depth=0):
        if not points:
            return None
        feature = depth % len(points[0])
        mid = len(points) // 2
        root = KDTreeNode(points[mid], build_kd_tree(points[:mid], depth + 1), build_kd_tree(points[mid + 1:], depth + 1))
        root.height = max(root.left.height, root.right.height) + 1
        return root
    kd_tree = build_kd_tree(X_train)

    def find_nearest_neighbors(point, kd_tree, k):
        if kd_tree is None:
            return []

        def recurse(node, current_distance, current_neighbors):
            if current_neighbors >= k:
                return []
            if current_neighbors >= k:
                return []
            if node.left is None and node.right is None:
                yield (node.point, current_distance)
                return
            yield from recurse(node.left, current_distance + math.hypot(point[feature], node.point[feature]), current_neighbors + 1)
            yield from recurse(node.right, current_distance + math.hypot(point[feature], node.point[feature]), current_neighbors + 1)
        neighbors = list(recurse(kd_tree, 0.0, 0))
        return neighbors

    def get_class(point, kd_tree):
        if kd_tree is None:
            return None
        if kd_tree.left is None and kd_tree.right is None:
            return y_train[kd_tree.point]
        class_counts = {}
        if kd_tree.left:
            left_class = get_class(point, kd_tree.left)
            class_counts[left_class] = class_counts.get(left_class, 0) + 1
        if kd_tree.right:
            right_class = get_class(point, kd_tree.right)
            class_counts[right_class] = class_counts.get(right_class, 0) + 1
        return max(class_counts, key=lambda x: class_counts[x])
    predictions = []
    for test_point in X_test:
        neighbors = find_nearest_neighbors(test_point, kd_tree, k)
        if not neighbors:
            predictions.append(0)
        else:
            classes = [y_train[i] for (point, dist) in neighbors for i in range(len(neighbors))]
            class_counts = Counter(classes)
            if len(class_counts) == 1:
                predictions.append(list(class_counts.keys())[0])
            else:
                most_common = max(class_counts, key=lambda x: class_counts[x])
                if class_counts[most_common] == class_counts.most_common()[1][1]:
                    predictions.append(min(class_counts.keys()))
                else:
                    predictions.append(most_common)
    return predictions

import math
def compute_ball_radius(centroid: list[float], X: list[list[float]]) -> float:
    """Compute the maximum Euclidean distance between the centroid and any data point in X.
    Returns the radius rounded to the nearest 4th decimal."""
    if not X:
        return 0.0
    max_distance = 0.0
    for point in X:
        distance = math.hypot(centroid[0] - point[0], centroid[1] - point[1])
        if distance > max_distance:
            max_distance = distance
    return round(max_distance, 4)

import numpy as np
def random_bernoulli_mab(n_arms: int=10) -> list[float]:
    """Randomly generates a Bernoulli MAB environment with n_arms arms."""
    if n_arms < 1:
        return []
    samples = np.random.uniform(0, 1, size=n_arms)
    normalized = samples / np.sum(samples)
    rounded = np.round(normalized, 4)
    return rounded.tolist()

import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """
    if isinstance(z, (int, float)):
        result = 1.0 / (1.0 + np.exp(-z))
        return round(result, 4)
    elif isinstance(z, (list, tuple)):
        z_array = np.array(z)
        result = 1.0 / (1.0 + np.exp(-z_array))
        return result.tolist()
    elif isinstance(z, np.ndarray):
        result = 1.0 / (1.0 + np.exp(-z))
        return result.round(4).tolist()
    else:
        raise ValueError('Input must be a scalar, list/tuple, or NumPy ndarray.')

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        return np.round(z * (1 - z), 4)
    if isinstance(z, np.ndarray):
        return np.round(z * (1 - z), 4)
    if isinstance(z, list):
        return [np.round(item * (1 - item), 4) for item in z]
    if not z:
        return []
    return [np.round(item * (1 - item), 4) for item in z]

import numpy as np
def relu_grad(z):
    """Compute the element-wise derivative of the ReLU activation.

    The function should return 0 for every entry that is less than or equal to
    zero and 1 for every entry that is strictly greater than zero.  Scalars
    must yield a single integer; arrays/lists must yield a list of identical
    shape.
    """
    if isinstance(z, (int, float)):
        return 1 if z > 0 else 0
    elif isinstance(z, np.ndarray):
        return np.where(z > 0, 1, 0)
    else:
        return np.where(z > 0, 1, 0).tolist()

import os
from typing import List, Union
def extract_archive(file_path: str, path: str='.', archive_format: Union[str, List[str], None]='auto') -> bool:
    """Checks whether *file_path* belongs to an allowed archive format.

    The function MUST NOT raise any exception and MUST NOT perform real file
    extraction.  Read the task description for the full specification.

    Args:
        file_path: Full path (or name) of the archive file.
        path: Destination folder – ignored in this simplified version.
        archive_format: See task description.

    Returns:
        True if the file name matches one of the allowed formats, False otherwise.
    """
    file_name = file_path.lower()
    allowed_formats = []
    if archive_format is None or archive_format == '':
        allowed_formats = []
    elif isinstance(archive_format, str):
        allowed_formats = [archive_format]
    elif isinstance(archive_format, list):
        allowed_formats = archive_format
    if not allowed_formats:
        return False
    if '.' in file_name:
        extension = file_name.split('.')[-1]
    else:
        extension = ''
    for fmt in allowed_formats:
        if fmt in ['tar', 'zip'] and extension in ['tar', 'tar.gz', 'tgz', 'tar.bz', 'tar.bz2', 'tbz']:
            return True
        if fmt == 'zip' and extension == 'zip':
            return True
    return False

import numpy as np
def gaussian_likelihood(x, mean, sigma):
    """Compute the probability density of a normal distribution.

    Parameters
    ----------
    x : float | list[float] | np.ndarray
        Point(s) where the density will be evaluated.
    mean : float
        The mean (µ) of the distribution.
    sigma : float
        The standard deviation (σ) of the distribution.

    Returns
    -------
    float | list[float]
        Density value(s) rounded to 4 decimal places. A single float is
        returned for a scalar x, otherwise a list is returned.
    """
    if sigma <= 0:
        raise ValueError('Sigma must be greater than 0.')
    if isinstance(x, (float, int)):
        result = np.exp(-(x - mean) ** 2 / (2 * sigma ** 2)) / (sigma * np.sqrt(2 * np.pi))
        return round(result, 4)
    elif isinstance(x, np.ndarray):
        result = np.exp(-(x - mean) ** 2 / (2 * sigma ** 2)) / (sigma * np.sqrt(2 * np.pi))
        return result.tolist()
    elif isinstance(x, list):
        result = np.exp(-(np.array(x) - mean) ** 2 / (2 * sigma ** 2)) / (sigma * np.sqrt(2 * np.pi))
        return result.tolist()
    else:
        raise TypeError('x must be a float, list, or numpy array.')

import numpy as np
def sigmoid_(Z):
    """Compute the logistic sigmoid activation element-wise.

    Args:
        Z: A scalar, 1-D list/array, or 2-D list/array of real numbers.

    Returns:
        Sigmoid-transformed value(s) rounded to 4 decimals.  Returns a float for
        scalar input; otherwise returns a Python list preserving the input
        shape.
    """
    if isinstance(Z, (int, float)):
        Z = np.array([Z])
    else:
        Z = np.array(Z)
    S = 1 / (1 + np.exp(-Z))
    S_rounded = np.round(S, 4)
    if isinstance(Z, (int, float)):
        return round(S_rounded[0], 4)
    else:
        return S_rounded.tolist()

from collections import Counter
from typing import Any, Dict, List
def build_tree(data: List[List[Any]]) -> Dict[str, Any]:
    """Build a CART decision tree using the Gini impurity criterion.

    The *data* argument is a list where every inner list represents a training
    sample.  All but the last elements of each sample are feature values; the
    last element is the class label.

    The function should return the root of the generated tree expressed as a
    nested dictionary:
        Leaf   -> {"label": class_label}
        Branch -> {"feature": index,
                    "value":   split_value,
                    "left":    <subtree for samples with feature == value>,
                    "right":   <subtree for samples with feature != value>}

    If *data* is empty return the empty dictionary.
    """
    if not data:
        return {}

    def gini_impurity(samples):
        class_counts = Counter((sample[-1] for sample in samples))
        total_samples = len(samples)
        if total_samples == 0:
            return 0.0
        return sum((-count * (count / total_samples) ** 2 for count in class_counts.values()))

    def try_splits(data, feature_idx):
        unique_values = sorted(set((sample[feature_idx] for sample in data)))
        splits = []
        for value in unique_values:
            left_samples = [sample for sample in data if sample[feature_idx] == value]
            right_samples = [sample for sample in data if sample[feature_idx] != value]
            if not left_samples and (not right_samples):
                continue
            splits.append((value, left_samples, right_samples))
        return splits

    def best_split(data):
        best_gain = -1.0
        best_split = None
        splits = try_splits(data, 0)
        for (value, left, right) in splits:
            original_impurity = gini_impurity(data)
            left_impurity = gini_impurity(left)
            right_impurity = gini_impurity(right)
            gain = original_impurity - (left_impurity + right_impurity) / 2
            if gain > best_gain:
                best_gain = gain
                best_split = (value, left, right)
        return best_split

    def build_tree_recursive(data):
        if not data:
            return {}
        current_impurity = gini_impurity(data)
        best_split = best_split(data)
        if not best_split:
            class_counts = Counter((sample[-1] for sample in data))
            return {'label': class_counts.most_common(1)[0][0]}
        (feature, value, left, right) = best_split
        internal_node = {'feature': feature, 'value': value, 'left': build_tree_recursive(left), 'right': build_tree_recursive(right)}
        return internal_node
    return build_tree_recursive(data)

import numpy as np
from collections import deque
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """
    Density-Based Spatial Clustering of Applications with Noise (DBSCAN)

    Parameters:
    data (np.ndarray): 2D array of shape (n_samples, n_features)
    eps (float): Maximum distance between two points to be considered part of the same cluster
    min_samples (int): Minimum number of points required to form a dense region (core point)

    Returns:
    list[int]: Cluster labels for each sample. Noise points are labeled -1.
    """
    data = data.copy()
    (n_samples, n_features) = data.shape
    labels = [-1] * n_samples
    cluster_id = 0
    neighborhood = {}
    distances = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            distances[i, j] = np.sqrt(np.sum((data[i] - data[j]) ** 2))
    for i in range(n_samples):
        neighborhood[i] = []
        for j in range(n_samples):
            if distances[i, j] <= eps:
                neighborhood[i].append(j)
    core_points = []
    for i in range(n_samples):
        if len(neighborhood[i]) >= min_samples:
            core_points.append(i)
    queue = deque()
    visited = set()
    for point in core_points:
        if labels[point] == -1:
            labels[point] = cluster_id
            queue.append(point)
            while queue:
                current = queue.popleft()
                visited.add(current)
                for neighbor in neighborhood[current]:
                    if labels[neighbor] == -1 and neighbor not in visited:
                        labels[neighbor] = cluster_id
                        queue.append(neighbor)
                        visited.add(neighbor)
    for i in range(n_samples):
        if labels[i] == -1:
            labels[i] = -1
    return [label + 1 if label != -1 else -1 for label in labels]

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if task_type == 'classification':
        if root.y is not None:
            return root.y
        feature = features[root.label]
        for child in root.child:
            if child.x == feature:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        if root.y is not None:
            return root.y
        feature = features[root.label]
        if feature <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)
    else:
        raise ValueError("Invalid task_type. Must be 'classification' or 'regression'.")

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) for each sample in the dataset."""
    n_samples = data.shape[0]
    n_features = data.shape[1]
    distances = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            distances[i, j] = np.sqrt(np.sum((data[i] - data[j]) ** 2))
    k_nearest_neighbors = []
    k_distances = []
    for i in range(n_samples):
        dists = distances[i, :]
        sorted_indices = np.argsort(dists)
        k_nearest_indices = sorted_indices[-k:]
        k_nearest_neighbors.append(k_nearest_indices)
        k_distances.append(dists[sorted_indices[-k]])
    reach_dist = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            if i != j:
                reach_dist[i, j] = max(k_distances[i][j], distances[i, j])
    lrd = np.zeros(n_samples)
    for i in range(n_samples):
        neighbors = k_nearest_neighbors[i]
        denominator = 0.0
        for j in neighbors:
            denominator += reach_dist[i, j]
        if denominator == 0:
            lrd[i] = 0.0
        else:
            lrd[i] = k / denominator
    lof = np.zeros(n_samples)
    for i in range(n_samples):
        neighbors = k_nearest_neighbors[i]
        numerator = 0.0
        for j in neighbors:
            numerator += lrd[j]
        lof[i] = numerator / k / lrd[i]
    lof_rounded = np.round(lof, 4)
    outlier_indices = np.where(lof_rounded > epsilon)[0]
    return (lof_rounded, outlier_indices)

import numpy as np
def minesweeper_step(ground: list[list[float | int]], flags: list[list[bool]]) -> tuple[list[tuple[int, int]], list[tuple[int, int]]]:
    """Perform one logical deduction step on a Minesweeper board.

    Args:
        ground: A 2D list of floats (0-8 or np.nan) representing the visible part of the board.
        flags: A 2D list of booleans representing the already placed flags.

    Returns:
        A tuple of two lists:
        - clicks: List of coordinates that can be safely revealed.
        - new_flags: List of coordinates that must be flagged.
    """
    (rows, cols) = (len(ground), len(ground[0]))
    clicks = []
    new_flags = []
    grid = np.array(ground)
    flag_grid = np.array(flags)

    def get_neighbors(r, c):
        neighbors = []
        for dr in [-1, 0, 1]:
            for dc in [-1, 0, 1]:
                if dr == 0 and dc == 0:
                    continue
                (nr, nc) = (r + dr, c + dc)
                if 0 <= nr < rows and 0 <= nc < cols:
                    neighbors.append((nr, nc))
        return neighbors
    for r in range(rows):
        for c in range(cols):
            if not np.isnan(grid[r][c]):
                if not flag_grid[r][c]:
                    value = grid[r][c]
                    bombs = 0
                    unexplored = 0
                    for (nr, nc) in get_neighbors(r, c):
                        if flag_grid[nr][nc]:
                            bombs += 1
                        elif np.isnan(grid[nr][nc]):
                            unexplored += 1
                    if value == 0:
                        for (nr, nc) in get_neighbors(r, c):
                            if np.isnan(grid[nr][nc]) and (not flag_grid[nr][nc]):
                                clicks.append((nr, nc))
                    elif value == 8:
                        for (nr, nc) in get_neighbors(r, c):
                            if np.isnan(grid[nr][nc]) and (not flag_grid[nr][nc]):
                                new_flags.append((nr, nc))
                    elif bombs == value:
                        for (nr, nc) in get_neighbors(r, c):
                            if np.isnan(grid[nr][nc]) and (not flag_grid[nr][nc]):
                                clicks.append((nr, nc))
                    elif unexplored == value:
                        for (nr, nc) in get_neighbors(r, c):
                            if np.isnan(grid[nr][nc]) and (not flag_grid[nr][nc]):
                                new_flags.append((nr, nc))
    clicks.sort()
    new_flags.sort()
    return (clicks, new_flags)

from copy import deepcopy
from collections import defaultdict
def deduce_mines(constraints: list[list]) -> tuple[list[str], list[str]]:
    """Simplify Minesweeper constraints.

    Args:
        constraints: A list of 2-element lists [variables, value] representing constraints.

    Returns:
        A tuple (safe, mines) where:
        - safe is a sorted list of all variables that are certainly safe
        - mines is a sorted list of all variables that are certainly mines
    """
    safe_vars = set()
    mine_vars = set()
    variable_status = {}
    processed_constraints = []
    unique_constraints = set()
    for constraint in constraints:
        (variables, value) = constraint
        if (tuple(variables), value) not in unique_constraints:
            unique_constraints.add((tuple(variables), value))
            processed_constraints.append((variables, value))
    for (var_set, val) in processed_constraints:
        if val == 0:
            for var in var_set:
                safe_vars.add(var)
        elif val == len(var_set):
            for var in var_set:
                mine_vars.add(var)
    queue = list(mine_vars.union(safe_vars))
    while queue:
        var = queue.pop(0)
        if var in safe_vars:
            continue
        if var in mine_vars:
            continue
        for c in processed_constraints:
            if var in c[0]:
                new_vars = [v for v in c[0] if v != var]
                new_val = c[1] - 1 if var in mine_vars else c[1]
                if new_vars:
                    new_constraint = (new_vars, new_val)
                    if new_constraint not in unique_constraints:
                        unique_constraints.add(new_constraint)
                        processed_constraints.remove(c)
                        processed_constraints.append(new_constraint)
                        queue.append(var)
        for c in processed_constraints:
            (s1, v1) = c
            (s2, v2) = processed_constraints[processed_constraints.index(c) + 1]
            if s1.issubset(s2):
                new_s = s2 - s1
                new_v = v2 - v1
                if new_s and new_v > 0:
                    if (tuple(new_s), new_v) not in unique_constraints:
                        unique_constraints.add((tuple(new_s), new_v))
                        processed_constraints.remove(c)
                        processed_constraints.append((new_s, new_v))
                        queue.append(var)
    unique_constraints = set()
    for c in processed_constraints:
        if (tuple(c[0]), c[1]) not in unique_constraints:
            unique_constraints.add((tuple(c[0]), c[1]))
    safe_list = sorted(safe_vars)
    mine_list = sorted(mine_vars)
    return (safe_list, mine_list)

def maze_to_graph(maze: list[list[int]]) -> dict[tuple[int, int], list[tuple[int, int]]]:
    """
    Convert a binary maze into an adjacency-list graph.

    Args:
        maze: A 2D list of integers (0s and 1s) representing the maze.

    Returns:
        A dictionary mapping each open cell (row, col) to a sorted list of its
        orthogonal neighbors (open cells).
    """
    graph = {}
    for row in range(len(maze)):
        for col in range(len(maze[0])):
            if maze[row][col] == 0:
                graph[row, col] = []
                for (dr, dc) in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                    (nr, nc) = (row + dr, col + dc)
                    if 0 <= nr < len(maze) and 0 <= nc < len(maze[0]) and (maze[nr][nc] == 0):
                        graph[row, col].append((nr, nc))
    for node in graph:
        graph[node].sort()
    return graph

import numpy as np
def convert_maze_to_graph(maze: np.ndarray) -> dict:
    """
    Convert a binary maze to an adjacency list representation.

    Args:
        maze: A 2-D NumPy array of 0s and 1s.

    Returns:
        A dictionary representing the adjacency list of the maze-graph.
    """
    free_cells = np.where(maze == 1)
    if not free_cells[0].size:
        return {}
    adjacency = {}
    for (row, col) in zip(free_cells[0], free_cells[1]):
        neighbors = []
        if row > 0 and maze[row - 1, col] == 1:
            neighbors.append((row - 1, col))
        if row < maze.shape[0] - 1 and maze[row + 1, col] == 1:
            neighbors.append((row + 1, col))
        if col > 0 and maze[row, col - 1] == 1:
            neighbors.append((row, col - 1))
        if col < maze.shape[1] - 1 and maze[row, col + 1] == 1:
            neighbors.append((row, col + 1))
        neighbors.sort()
        adjacency[row, col] = neighbors
    return adjacency
